css:
#secure_prescription {
	width: 20px;
	height: 20px;
	background: #eee;
	color: #36414c;
	font-weight: bold;
	border: 1px solid #d1d8dd;
}

html:
{% include "templates/includes/prescription_header.html" %}

<div>
	<div id="footer-html" class="visible-pdf">
		{% if not no_letterhead and footer %}
		<div class="letter-head-footer">
			{{ footer }}
		</div>
		{% endif %}
		<p class="text-center small page-number visible-pdf">
			{{ _("Page {0} of {1}").format('<span class="page"></span>', '<span class="topage"></span>') }}
		</p>
	</div>

	<table>
		<tbody>
			<tr>
				{%- for row in doc.drug_prescription_table -%}
				<td style="width: 20px;font-weight: bold;font-size: 11pt;">{{ row.idx }}.</td>
				<td style="width: 300px; font-weight: bold;font-size: 11pt;">{{ row.drug }}</td>
				{% if row.pharmaceutical_form %}
				<td style="width: 60px;">{{ row.pharmaceutical_form }}</td>
				{% else %}
				<td style="width: 60px;"></td>
				{% endif %}
				{% if row.posology %}
				<td style="width: 130px;">{{ row.posology }}</td>
				{% else %}
				<td style="width: 130px;"></td>
				{% endif %}
				{% if row.treatment_duration %}
				<td style="width: 130px;text-align:right;">{{ row.treatment_duration }}</td>
				{% else %}
				<td style="width: 130px;"></td>
				{% endif %}
			</tr>
			<tr>
				<td style="width: 20px;"></td>
				{% if row.additional_notes %}
				<td style="width: 300px; font-style: italic;">{{ row.additional_notes }}</td>
				{% else %}
				<td style="width: 300px;"></td>
				{% endif %}
				{% if row.pharmaceutical_form %}
				<td style="width: 60px;"></td>
				{% endif %}
				{% if row.posology %}
				<td style="width: 130px;"></td>
				{% endif %}
				{% if row.treatment_duration %}
				<td style="width: 130px;"></td>
				{% endif %}
			</tr>
			{%- endfor -%}
		</tbody>
	</table>

	<div class="row text-center">
		<div class="col-xs-1 col-xs-offset-11">
			<div id="secure_prescription">{{ doc.drug_prescription_table|length }}</div>
		</div>
	</div>
</div>
