css:
.col-xs-4, .col-xs-6, .col-xs-8, .col-xs-12 {
  padding-top: 15px;
}

table, tr, td, th, tbody, thead, tfoot, .col-xs-4, .col-xs-6, .col-xs-8, .col-xs-12 {
    page-break-inside: avoid !important;
}

html:
{% include "templates/includes/folder_header.html" %}

<div>
	<div id="footer-html" class="visible-pdf">
		{% if not no_letterhead and footer %}
		<div class="letter-head-footer">
			{{ footer }}
		</div>
		{% endif %}
		<p class="text-center small page-number visible-pdf">
			{{ _("Page {0} of {1}").format('<span class="page"></span>', '<span class="topage"></span>') }}
		</p>
	</div>
	{% if doc.mother_general_health or doc.mother_pain_rating%}
	<div><h4>{{ _("Mother") }}</h4></div>
	<div class="row data-field">
		{% if doc.mother_general_health %}
	<div class="col-xs-4">
		<label>{{ _("General Health") }}</label>
		{{ doc.mother_general_health }}</div>
		{% endif %}
		{% if doc.mother_pain_rating %}
	<div class="col-xs-4">
		<label>{{ _("Mother Pain Rating") }}</label>
		{{ doc.mother_pain_rating }}</div>
		{% endif %}
	</div>
	<br>
	{% endif %}

 {% if doc.blood_pressure or doc.edema or doc.functional_signs_of_hypertension or doc.pulsations or doc.urinary_functional_signs or doc.mother_temperature or doc.digestive_functional_signs %}
	<div class="row data-field">
		{% if doc.blood_pressure %}
		<div class="col-xs-4">
			<label>{{ _("Blood Pressure") }}</label>
			{{ doc.blood_pressure }}</div>
		{% endif %}
		{% if doc.pulsations %}
		<div class="col-xs-4">
			<label>{{ _("Pulsations") }}</label>
			{{ doc.pulsations }}</div>
		{% endif %}
		{% if doc.mother_temperature %}
		<div class="col-xs-4">
			<label>{{ _("Temperature") }}</label>
			{{ doc.mother_temperature }}</div>
		{% endif %}
		{% if doc.edema_details %}
		<div class="col-xs-4">
			<label>{{ _("Edema") }}</label>
			{{ doc.edema_details }}</div>
		{% endif %}
		{% if doc.ufs_details %}
		<div class="col-xs-4">
			<label>{{ _("Urinary Functional Signs") }}</label>
			{{ doc.ufs_details }}</div>
		{% endif %}
		{% if doc.dfs_details %}
		<div class="col-xs-4">
			<label>{{ _("Digestive Functional Signs") }}</label>
			{{ doc.dfs_details }}</div>
		{% endif %}
		{% if doc.functional_signs_of_hypertension_details %}
		<div class="col-xs-4">
			<label>{{ _("Functional Signs of Hypertension") }}</label>
			{{ doc.functional_signs_of_hypertension_details }}</div>
		{% endif %}
	</div>
	<hr>
	<br>
	{% endif %}
	{% if doc.breasts or doc.uterus or doc.lochia or doc.suture_scar %}
 	<div class="row data-field">
 		{% if doc.breasts %}
 		<div class="col-xs-4">
 			<label>{{ _("Breasts") }}</label>
 			{{ doc.breasts }}</div>
 		{% endif %}
 		{% if doc.uterus %}
 		<div class="col-xs-4">
 			<label>{{ _("Uterus") }}</label>
 			{{ doc.uterus }}</div>
 		{% endif %}
		{% if doc.lochia %}
		<div class="col-xs-4">
			<label>{{ _("Lochia") }}</label>
			{{ doc.lochia }}</div>
		{% endif %}
		{% if doc.suture_scar %}
		<div class="col-xs-4">
			<label>{{ _("Suture/C-Section Scar") }}</label>
			{{ doc.suture_scar }}</div>
		{% endif %}
	</div>
	<hr>
 	<br>
 	{% endif %}
	{% if doc.psychological_state %}
 	<div class="row data-field">
 		<div class="col-xs-12">
 			<label>{{ _("Psychological State/Mother-Child Bond") }}</label>
 			{{ doc.get_formatted("psychological_state") }}</div>
	</div>
 	<br>
 	{% endif %}

	{% if doc.drugs|length != 0 %}
			<div class="col-xs-12">
			<table class="table table-condensed">
				<tr>
					<th>{{ _("Drug") }}</th>
					<th>{{ _("Notes") }}</th>
				</tr>
				<tbody>
					{%- for row in doc.drugs -%}
					<tr>
						<td style="width: 100px;">{{ row.drug or '' }}</td>
						<td style="width: 200px;">{{ row.notes or '' }}</td>
					</tr>
					{% endfor %}
				</tbody>
			</table>
		</div>
		<br>
		{% endif %}
	</div>
	{% if doc.education_advices %}
 	<div class="row data-field">
 		<div class="col-xs-12">
 			<label>{{ _("Education/Advices") }}</label>
 			{{ doc.get_formatted("education_advices") }}</div>
	</div>
	<hr>
 	<br>
 	{% endif %}
	{% if doc.prescription %}
 	<div class="row data-field">
 		<div class="col-xs-12">
 			<label>{{ _("Prescription Written") }}</label>
 			{{ doc.get_formatted("prescription") }}</div>
	</div>
	<hr>
 	<br>
 	{% endif %}
	{% if doc.newborn_general_health or doc.newborn_temperature %}
	<div><h4>{{ _("Newborn") }}</h4></div>
	<div class="row data-field">
		{% if doc.newborn_general_health %}
		<div class="col-xs-4">
			<label>{{ _("General Health") }}</label>
			{{ doc.newborn_general_health }}</div>
		{% endif %}
		{% if doc.newborn_temperature %}
		<div class="col-xs-4">
			<label>{{ _("Temperature") }}</label>
			{{ doc.newborn_temperature }}</div>
		{% endif %}
	</div>
	<hr>
	<br>
	{% endif %}
	{% if doc.cardiac_frequency or doc.respiratory_rate %}
	<div><h5>{{ _("Newborn Exam") }}</h5></div>
	<div class="row data-field">
		{% if doc.cardiac_frequency %}
		<div class="col-xs-4">
			<label>{{ _("Cardiac Frequency") }}</label>
			{{ doc.cardiac_frequency }}</div>
		{% endif %}
		{% if doc.respiratory_rate %}
		<div class="col-xs-4">
			<label>{{ _("Respiratory Rate") }}</label>
			{{ doc.respiratory_rate }}</div>
		{% endif %}
	</div>
	<br>
	{% endif %}
	{% if doc.normal_neurological_morphological_exam or doc.perceived_femoral_pulse or doc.abnormal_neurological_morphological_exam or doc.perceived_femoral_pulse_details %}
	<div class="row data-field">
		{% if doc.normal_neurological_morphological_exam %}
		<div class="col-xs-4">
			<label>{{ _("Normal Neurological and Morphological Exam") }}</label>
			{{ _("Yes") }}</div>
		{% elif doc.abnormal_neurological_morphological_exam %}
			<div class="col-xs-4">
				<label>{{ _("Abnormal Neurological and Morphological Exam") }}</label>
				{{ doc.abnormal_neurological_morphological_exam }}</div>
		{% endif %}
		{% if doc.perceived_femoral_pulse %}
		<div class="col-xs-4">
			<label>{{ _("Femoral Pulse Perceived on Both Sides") }}</label>
			{{ _("Yes") }}</div>
		{% elif doc.perceived_femoral_pulse_details %}
		<div class="col-xs-4">
			<label>{{ _("If Femoral Pulse Not Perceived:") }}</label>
			{{ doc.perceived_femoral_pulse_details }}</div>
		{% endif %}
	</div>
	<hr>
	<br>
	{% endif %}
	{% if doc.feeding_type or doc.effective_feeding_criterion or doc.number_meals_per_day %}
	<div><h5>{{ _("Feeding") }}</h5></div>
	<div class="row data-field">
		{% if doc.feeding_type %}
		<div class="col-xs-4">
			<label>{{ _("Feeding Type") }}</label>
			{{ _(doc.feeding_type) }}</div>
		{% endif %}
		{% if doc.effective_feeding_criterion %}
		<div class="col-xs-4">
			<label>{{ _("Effective Feeding Criterion") }}</label>
			{{ doc.effective_feeding_criterion }}</div>
		{% endif %}
		{% if doc.number_meals_per_day %}
		<div class="col-xs-4">
			<label>{{ _("Number of Meals per Day") }}</label>
			{{ doc.number_meals_per_day }}</div>
		{% endif %}
	</div>
	<hr>
	<br>
	{% endif %}
	{% if doc.stools_urine or doc.vomiting or doc.jaundice_risk or doc.pain_rating or doc.vitamin_supplement or doc.stools_urine_details  %}
	<div class="row data-field">
		{% if doc.stools_urine %}
		<div class="col-xs-4">
			<label>{{ _("Stools/Urine") }}</label>
			{{ _("Yes") }}</div>
		{% elif doc.stools_urine_details %}
		<div class="col-xs-4">
			<label>{{ _("Stools/Urine Issue:") }}</label>
			{{ doc.stools_urine_details }}</div>
		{% endif %}
		{% if doc.vomiting_details %}
		<div class="col-xs-4">
			<label>{{ _("Vomiting Details:") }}</label>
			{{ doc.vomiting_details }}</div>
		{% endif %}
		{% if doc.jaundice_risk_details %}
		<div class="col-xs-4">
			<label>{{ _("Jaundice Details:") }}</label>
			{{ doc.jaundice_risk_details }}</div>
		{% endif %}
		{% if doc.pain_rating_details %}
		<div class="col-xs-4">
			<label>{{ _("Pain Rating Details:") }}</label>
			{{ doc.pain_rating_details }}</div>
		{% endif %}
	</div>
	<hr>
	<br>
	{% endif %}
	{% if doc.weight_of_the_day or doc.weight_gain %}
	<div class="row data-field">
		{% if doc.weight_of_the_day %}
		<div class="col-xs-4">
			<label>{{ _("Weight of the Day") }}</label>
			{{ doc.weight_of_the_day }}</div>
		{% endif %}
		{% if doc.weight_gain %}
		<div class="col-xs-4">
			<label>{{ _("Weight Gain") }}</label>
			{{ doc.weight_gain }}</div>
		{% endif %}
	</div>
	<hr>
	<br>
	{% endif %}
	{% if doc.comments %}
	<div><h4>{{ _("Comments") }}</h4></div>
	<div class="row data-field">
		<div class="col-xs-12">
			{{ doc.comments }}</div>
	</div>
	<br>
	{% endif %}
</div>
