css:
#secure_prescription {
	width: 20px;
	height: 20px;
	background: #eee;
	color: #36414c;
	font-weight: bold;
	border: 1px solid #d1d8dd;
}

html:
{% include "templates/includes/prescription_header.html" %}

<div>
	<div id="footer-html" class="visible-pdf">
		{% if not no_letterhead and footer %}
		<div class="letter-head-footer">
			{{ footer }}
		</div>
		{% endif %}
		<p class="text-center small page-number visible-pdf">
			{{ _("Page {0} of {1}").format('<span class="page"></span>', '<span class="topage"></span>') }}
		</p>
	</div>
  <div style="font-size: 12pt;">{{ _("Faire Pratiquer :") }}</div>
	<br>
	<table>
		<tbody>
			<tr>
				{%- for row in doc.echography_prescription_table -%}
				<td style="width: 30px;"></td>
				<td style="width: 20px;font-weight: bold;font-size: 11pt;">{{ row.idx }}.</td>
				<td style="width: 300px;font-weight: bold;font-size: 11pt;">{{ row.echography_type }}</td>
				{% if row.additional_notes %}
				<td style="width: 300px;">{{ row.additional_notes }}</td>
				{% else %}
				<td style="width: 300px;"></td>
				{% endif %}
			</tr>
			{%- endfor -%}
		</tbody>
	</table>

	<div class="row text-center">
		<div class="col-xs-1 col-xs-offset-11">
			<div id="secure_prescription">{{ doc.lab_prescription_table|length }}</div>
		</div>
	</div>

	{% set practitioners = frappe.get_list("Professional Information Card", filters = {"user": doc.practitioner}, fields = ["signature"]) %}
	{%- for practitioner in practitioners -%}
	{% if practitioner.signature %}
	<br>
	<div class="row text-center">
		<div class="col-xs-5 col-xs-offset-7">
			<img src = {{practitioner.signature}} height = "200px" width = "200px">
		</div>
	</div>
	{% endif %}
	{%- endfor -%}
</div>
