css:
.col-xs-4, .col-xs-6, .col-xs-8, .col-xs-12 {
	padding-top: 15px;
}

table, tr, td, th, tbody, thead, tfoot, .col-xs-4, .col-xs-6, .col-xs-8, .col-xs-12, section {
	page-break-inside: avoid !important;
}

h1, h2, h3, h4, h5, h6, p {
	color: #434d56 !important;
}

label, div {
	color: #4f5a64 !important;
}

.print-heading {
	margin: 0.4in !important;
	text-align: left;
	text-transform: uppercase;
	padding-bottom: 20px;
	margin-bottom: 20px;
	border-bottom: 1px solid #d1d8dd;
}

.print-format {
	padding: 0;
	border-radius: 5px;
	margin: auto;
	position: relative;
}

.print-format th {
	background-color: #fff !important;
}

.header-html {
	padding: 0.4in;
	background: #f5fafc !important;
	border-radius: 5px 5px 0 0;
	width: 100%;
}

.body-html {
	padding: 0 0.8in 0.8in 0.8in;
}

.table > tbody + tbody {
	border-top: 1px solid #d1d8dd;
}

.table > tbody > tr > th {
	border-bottom: 1px solid #d1d8dd !important;
}

.table > tbody > tr > td {
	border-bottom: 1px solid #d1d8dd;
}

.vertical-table > tbody > tr > th {
	border-right: 1px solid #d1d8dd !important;
	width: 100px;
}

@media print {
	h1, h2, h3, h4, h5, h6, p, th, td, strong, a, b, ul, li {
		color: #434d56 !important;
	}
	.print-format {
	    padding: 0 25px;
	}
	.print-heading {
		margin-top: 25mm;
		margin-bottom: 25mm;
		color: #657381 !important;
	}
	.header-html {
		background: #f5fafc !important;
	}
	.body-html {
		padding: 0 0.4in 0.4in 0.4in !important;
	}
	.letter-head-footer {
		padding-top: 20px;
	}
	a[href]:after {
		content: none;
	}
}

html:
{% include "templates/includes/folder_header.html" %}

<div class="body-html">
	<section>
		{% if doc.consultation_purpose %}
		<div>
			<h4>{{ _("Consultation") }}</h4>
		</div>
		<div class="row data-field">
			<div class="col-xs-4">
				<label>{{ _("Consultation Purpose") }}</label>
				{{ doc.consultation_purpose }}</div>
		</div>
		<br>
		{% endif %}
	</section>
	<section>
		{% if doc.general_health or doc.date_of_last_menses or doc.current_treatments %}
		<div class="row data-field">
			{% if doc.general_health %}
			<div class="col-xs-4">
				<label>{{ _("General Health") }}</label>
				{{ doc.general_health }}</div>
			{% endif %}
			{% if doc.date_of_last_menses %}
			<div class="col-xs-4">
				<label>{{ _("Date of Last Menses") }}</label>
				{{ doc.date_of_last_menses }}</div>
			{% endif %}
			{% if doc.current_treatments %}
			<div class="col-xs-4">
				<label>{{ _("Current Treatments") }}</label>
				{{ doc.get_formatted("current_treatments") }}</div>
			{% endif %}
		</div>
		<br>
		{% endif %}
	</section>
	<section>
		{% if doc.high_blood_pressure or doc.lower_limbs or doc.weight or doc.cardiopulmonary_auscultation %}
		<div class="row data-field">
			{% if doc.high_blood_pressure %}
			<div class="col-xs-4">
				<label>{{ _("High Blood Pressure") }}</label>
				{{ doc.high_blood_pressure }}</div>
			{% endif %}
			{% if doc.lower_limbs %}
			<div class="col-xs-4">
				<label>{{ _("Lower Limbs") }}</label>
				{{ doc.lower_limbs }}</div>
			{% endif %}
			{% if doc.weight %}
			<div class="col-xs-4">
				<label>{{ _("Weight") }}</label>
				{{ doc.weight }}</div>
			{% endif %}
			{% if doc.cardiopulmonary_auscultation %}
			<div class="col-xs-4">
				<label>{{ _("Cardiopulmonary Auscultation") }}</label>
				{{ doc.cardiopulmonary_auscultation }}</div>
			{% endif %}
		</div>
		<br>
		{% endif %}
	</section>
	<section>
		{% if doc.breasts %}
		<div class="row data-field">
			<div class="col-xs-12">
				<label>{{ _("Breasts") }}</label>
				{{ doc.get_formatted("breasts") }}</div>
		</div>
		<br>
		{% endif %}
	</section>
	<section>
		{% if doc.thyroid or doc.abdomen %}
		<div class="row data-field">
			{% if doc.thyroid %}
			<div class="col-xs-6">
				<label>{{ _("Thyroid") }}</label>
				{{ doc.get_formatted("thyroid") }}</div>
			{% endif %}
			{% if doc.abdomen %}
			<div class="col-xs-6">
				<label>{{ _("Abdomen") }}</label>
				{{ doc.get_formatted("abdomen") }}</div>
			{% endif %}
		</div>
		<br>
		{% endif %}
	</section>
	<section>
		{% if doc.vulva_speculum or doc.vaginal_exam %}
		<div class="row data-field">
			{% if doc.vulva_speculum %}
			<div class="col-xs-6">
				<label>{{ _("Vulva/Speculum") }}</label>
				{{ doc.get_formatted("vulva_speculum") }}</div>
			{% endif %}
			{% if doc.vaginal_exam %}
			<div class="col-xs-6">
				<label>{{ _("Vaginal Exam") }}</label>
				{{ doc.get_formatted("vaginal_exam") }}</div>
			{% endif %}
		</div>
		<br>
		{% endif %}
	</section>
	<section>
		{% if doc.comments %}
		<div>
			<h4>{{ _("Comments") }}</h4>
		</div>
		<div class="row data-field">
			<div class="col-xs-12">
				{{ doc.get_formatted("comments") }}</div>
		</div>
		<br>
		{% endif %}
	</section>


	<div id="footer-html" class="visible-pdf">
		{% if not no_letterhead and footer %}
		<div class="letter-head-footer">
			{{ footer }}
		</div>
		{% endif %}
		<p class="text-center small page-number visible-pdf">
			{{ _("Page {0} of {1}").format('<span class="page"></span>', '<span class="topage"></span>') }}
		</p>
	</div>
</div>
