css:
.col-xs-4, .col-xs-6, .col-xs-8, .col-xs-12 {
  padding-top: 15px;
}

table, tr, td, th, tbody, thead, tfoot, .col-xs-4, .col-xs-6, .col-xs-8, .col-xs-12 {
    page-break-inside: avoid !important;
}

html:
{% include "templates/includes/folder_header.html" %}

<div>
	<div id="footer-html" class="visible-pdf">
		{% if not no_letterhead and footer %}
		<div class="letter-head-footer">
			{{ footer }}
		</div>
		{% endif %}
		<p class="text-center small page-number visible-pdf">
			{{ _("Page {0} of {1}").format('<span class="page"></span>', '<span class="topage"></span>') }}
		</p>
	</div>
	{% if doc.consultation_purpose %}
	<div><h4>{{ _("Consultation") }}</h4></div>
	<div class="row data-field">
	<div class="col-xs-4">
		<label>{{ _("Consultation Purpose") }}</label>
		{{ doc.consultation_purpose }}</div>
	</div>
	<br>
	{% endif %}

 {% if doc.general_health or doc.date_of_last_menses or doc.current_treatments %}
	<div class="row data-field">
		{% if doc.general_health %}
		<div class="col-xs-4">
			<label>{{ _("General Health") }}</label>
			{{ doc.general_health }}</div>
		{% endif %}
		{% if doc.date_of_last_menses %}
		<div class="col-xs-4">
			<label>{{ _("Date of Last Menses") }}</label>
			{{ doc.date_of_last_menses }}</div>
		{% endif %}
		{% if doc.current_treatments %}
		<div class="col-xs-4">
			<label>{{ _("Current Treatments") }}</label>
			{{ doc.current_treatments }}</div>
		{% endif %}
	</div>
	<br>
	{% endif %}
	{% if doc.high_blood_pressure or doc.lower_limbs or doc.weight or doc.cardiopulmonary_auscultation %}
 	<div class="row data-field">
 		{% if doc.high_blood_pressure %}
 		<div class="col-xs-4">
 			<label>{{ _("High Blood Pressure") }}</label>
 			{{ doc.high_blood_pressure }}</div>
 		{% endif %}
 		{% if doc.lower_limbs %}
 		<div class="col-xs-4">
 			<label>{{ _("Lower Limbs") }}</label>
 			{{ doc.lower_limbs }}</div>
 		{% endif %}
		{% if doc.weight %}
		<div class="col-xs-4">
			<label>{{ _("Weight") }}</label>
			{{ doc.weight }}</div>
		{% endif %}
		{% if doc.cardiopulmonary_auscultation %}
		<div class="col-xs-4">
			<label>{{ _("Cardiopulmonary Auscultation") }}</label>
			{{ doc.cardiopulmonary_auscultation }}</div>
		{% endif %}
	</div>
 	<br>
 	{% endif %}
	{% if doc.breasts %}
 	<div class="row data-field">
 		<div class="col-xs-12">
 			<label>{{ _("Breasts") }}</label>
 			{{ doc.breasts }}</div>
	</div>
 	<br>
 	{% endif %}
	{% if doc.thyroid or doc.abdomen %}
	<div class="row data-field">
		{% if doc.thyroid %}
		<div class="col-xs-6">
			<label>{{ _("Thyroid") }}</label>
			{{ doc.thyroid }}</div>
		{% endif %}
		{% if doc.abdomen %}
		<div class="col-xs-6">
			<label>{{ _("Abdomen") }}</label>
			{{ doc.abdomen }}</div>
		{% endif %}
	</div>
	<br>
	{% endif %}
	{% if doc.vulva_speculum or doc.vaginal_exam %}
	<div class="row data-field">
		{% if doc.vulva_speculum %}
		<div class="col-xs-6">
			<label>{{ _("Vulva/Speculum") }}</label>
			{{ doc.vulva_speculum }}</div>
		{% endif %}
		{% if doc.vaginal_exam %}
		<div class="col-xs-6">
			<label>{{ _("Vaginal Exam") }}</label>
			{{ doc.vaginal_exam }}</div>
		{% endif %}
	</div>
	<br>
	{% endif %}
	{% if doc.comments %}
	<div><h4>{{ _("Comments") }}</h4></div>
	<div class="row data-field">
		<div class="col-xs-12">
			{{ doc.comments }}</div>
	</div>
	<br>
	{% endif %}
</div>
