css:
#secure_prescription {
	width: 20px;
	height: 20px;
	background: #eee;
	color: #36414c;
	font-weight: bold;
	border: 1px solid #d1d8dd;
}

.col-xs-4, .col-xs-6, .col-xs-8, .col-xs-12 {
	padding-top: 15px;
}

table, td, th, tbody, thead, tfoot, .col-xs-4, .col-xs-6, .col-xs-8, .col-xs-12, section {
	page-break-inside: auto;
}

h1, h2, h3, h4, h5, h6, p {
	color: #434d56 !important;
}

label, div {
	color: #4f5a64 !important;
}

.print-heading {
	margin: 0.4in !important;
	text-align: left;
	text-transform: uppercase;
	padding-bottom: 20px;
	margin-bottom: 20px;
	border-bottom: 1px solid #d1d8dd;
}

.print-format {
	padding: 0;
	border-radius: 5px;
	margin: auto;
	position: relative;
}

.print-format th {
	background-color: #fff !important;
}

.header-html {
	background: #f5fafc !important;
	border-radius: 5px 5px 0 0;
	width: 100%;
}

.body-html {
	padding: 0 0.8in 0.8in 0.8in;
}

.table > tbody + tbody {
	border-top: 1px solid #d1d8dd;
}

.table > tbody > tr > th {
	border-bottom: 1px solid #d1d8dd !important;
}

.table > tbody > tr > td {
	border-bottom: 1px solid #d1d8dd;
}

.vertical-table > tbody > tr > th {
	border-right: 1px solid #d1d8dd !important;
	width: 100px;
}

@media print {
	h1, h2, h3, h4, h5, h6, p, th, td, strong, a, b, ul, li {
		color: #434d56 !important;
	}
	.print-format {
	    margin-top: 25mm;
	    margin-bottom: 25mm;
	}
	.print-heading {
		color: #657381 !important;
	}
	.header-html {
		background: #f5fafc !important;
	}
	.body-html {
		padding: 0 0.4in 0.4in 0.4in !important;
	}
	.letter-head-footer {
		padding-top: 20px;
	}
	a[href]:after {
		content: none;
	}
	tr {
        page-break-inside: avoid !important;
    }
}

html:
{% include "templates/includes/prescription_header.html" %}

<div class="body-html">
	<div style="font-size: 12pt;">{{ _("A Faire Pratiquer en Laboratoire :") }}</div>
	<br>
	<table>
		<tbody>
			<tr>
				{%- for row in doc.lab_prescription_table -%}
				<td style="width: 30px;"></td>
				<td style="width: 20px;font-weight: bold;">{{ row.idx }}.</td>
				<td style="width: 300px;font-weight: bold;">{{ row.lab_exam }}</td>
				{% if row.additional_notes %}
				<td style="width: 300px;">{{ row.additional_notes }}</td>
				{% else %}
				<td style="width: 300px;"></td>
				{% endif %}
			</tr>
			{%- endfor -%}
		</tbody>
	</table>

	<div class="row text-center">
		<div class="col-xs-1 col-xs-offset-11">
			<div id="secure_prescription">{{ doc.lab_prescription_table|length }}</div>
		</div>
	</div>

	{% set practitioners = frappe.get_list("Professional Information Card", filters = {"name": doc.practitioner}, fields =
	["signature"]) %}
	{%- for practitioner in practitioners -%}
	{% if practitioner.signature %}
	<br>
	<div class="row text-center">
		<div class="col-xs-5 col-xs-offset-7">
			<img src={{practitioner.signature}} height="200px" width="200px">
		</div>
	</div>
	{% endif %}
	{%- endfor -%}
	<div id="footer-html" class="visible-pdf">
		{% if not no_letterhead and footer %}
		<div class="letter-head-footer">
			{{ footer }}
		</div>
		{% endif %}
		<p class="text-center small page-number visible-pdf">
			{{ _("Page {0} of {1}").format('<span class="page"></span>', '<span class="topage"></span>') }}
		</p>
	</div>
</div>
