css:
.col-xs-4, .col-xs-6, .col-xs-8, .col-xs-12 {
  padding-top: 15px;
}

table, tr, td, th, tbody, thead, tfoot {
    page-break-inside: avoid !important;
}

html:
{% include "templates/includes/patient_folder_header.html" %}

<div>
	<div id="footer-html" class="visible-pdf">
		{% if not no_letterhead and footer %}
		<div class="letter-head-footer">
			{{ footer }}
		</div>
		{% endif %}
		<p class="text-center small page-number visible-pdf">
			{{ _("Page {0} of {1}").format('<span class="page"></span>', '<span class="topage"></span>') }}
		</p>
	</div>
 {% if doc.patient_birth_name or doc.patient_date_of_birth or doc.patient_age or doc.address_html or doc.profession or doc.current_activity_status or doc.commuting_time %}
	<div class="row data-field">
		{% if doc.salutation or doc.patient_birth_name %}
		<div class="col-xs-12">
			<label>{{ _("Title and Birth Name") }}</label>
			{{ _(doc.salutation) }} {{ doc.patient_birth_name or '' }}
		</div>
		{% endif %}
		{% if doc.origin %}
		<div class="col-xs-12">
			<label>{{ _("Origin") }}</label>
			{{ doc.origin }}
		</div>
		{% endif %}
		{% if doc.patient_date_of_birth %}
		<div class="col-xs-4">
			<label>{{ _("Date of Birth") }}</label>
			{{ doc.patient_date_of_birth }}
		</div>
		{% endif %}
		{% if doc.patient_age %}
		<div class="col-xs-4">
			<label>{{ _("Age") }}</label>
			{{ doc.patient_age }}
		</div>
		{% endif %}
		{% if doc.social_security_number %}
		<div class="col-xs-4">
			<label>{{ _("Social Security Number") }}</label>
			{{ doc.social_security_number }}
		</div>
		{% endif %}
		{% if doc.address_html %}
		<div class="col-xs-4">
			<label>{{ _("Adress") }}</label>
			{{ doc.address_html }}
		</div>
		{% endif %}
		{% if doc.profession %}
		<div class="col-xs-4">
			<label>{{ _("Profession") }}</label>
			{{ doc.profession }}
		</div>
			{% endif %}
			{% if doc.current_activity_status %}
		<div class="col-xs-4">
			<label>{{ _("Current Activity Status") }}</label>
			{{ doc.current_activity_status }}
		</div>
			{% endif %}
			{% if doc.commuting_time %}
		<div class="col-xs-4">
			<label>{{ _("Commuting Time in Mins") }}</label>
			{{ doc.commuting_time }}
		</div>
			{% endif %}
	</div>
	<br>
 {% endif %}
{% if doc.blood_group or doc.valid_group_card or doc.urinary_infection_details or doc.high_blood_pressure_details or doc.ophtalmology_details or doc.ophtalmology_details or doc.migraine_details or doc.diabetes_cholesterol_cholestasis_details or doc.cerebrovascular_accident_aneurysm_phlebitis_details or doc.myocardial_infarction_cardiopathy_details or doc.hormonal_dependent_cancer_details or doc.long_term_disease or doc.allergies|length != 0 %}
<div><h4>{{ _("Medical Background") }}</h4></div>
<div class="row data-field">
	{% if doc.blood_group %}
	<div class="col-xs-4">
		<label>{{ _("Blood Group") }}</label>
		{{ doc.blood_group }}</div>
	{% endif %}
	{% if doc.valid_group_card %}
	<div class="col-xs-4">
		<label>{{ _("Valid Group Card") }}</label>
	</div>
	{% endif %}
	{% if doc.urinary_infection_details %}
	<div class="col-xs-4">
		<label>{{ _("Urinary Infection") }}</label>
		{{ doc.urinary_infection_details }}</div>
	{% endif %}
	{% if doc.high_blood_pressure_details %}
	<div class="col-xs-4">
		<label>{{ _("High Blood Pressure") }}</label>
		{{ doc.high_blood_pressure_details }}</div>
	{% endif %}
	{% if doc.ophtalmology_details %}
	<div class="col-xs-4">
		<label>{{ _("Ophtalmology") }}</label>
		{{ doc.ophtalmology_details }}</div>
	{% endif %}
	{% if doc.migraine_details %}
	<div class="col-xs-4">
		<label>{{ _("Migraine") }}</label>
		{{ doc.migraine_details }}</div>
	{% endif %}
	{% if doc.diabetes_cholesterol_cholestasis_details %}
	<div class="col-xs-4">
		<label>{{ _("Diabetes, Cholesterol, Cholestasis") }}</label>
		{{ doc.diabetes_cholesterol_cholestasis_details }}</div>
	{% endif %}
	{% if doc.cerebrovascular_accident_aneurysm_phlebitis_details %}
	<div class="col-xs-4">
		<label>{{ _("Cerebrovascular Accident, Aneurysm, Phlebitis, Varicose Veins") }}</label>
		{{ doc.cerebrovascular_accident_aneurysm_phlebitis_details }}</div>
	{% endif %}
	{% if doc.myocardial_infarction_cardiopathy_details %}
	<div class="col-xs-4">
		<label>{{ _("Myocardial Infarction, Cardiopathy") }}</label>
		{{ doc.myocardial_infarction_cardiopathy_details }}</div>
	{% endif %}
	{% if doc.hormonal_dependent_cancer_details %}
	<div class="col-xs-4">
		<label>{{ _("Hormonal Dependent Cancer") }}</label>
		{{ doc.hormonal_dependent_cancer_details }}</div>
	{% endif %}
	{% if doc.long_term_disease %}
	<div class="col-xs-12">
		<label>{{ _("Long Term Disease,  Hospitalisation Or Treatment") }}</label>
		{{ doc.long_term_disease }}</div>
	{% endif %}
	{% if doc.allergies|length != 0 %}
			<div class="col-xs-8">
			<table class="table table-condensed">
				<tr>
					<th>{{ _("Allergies") }}</th>
					<th>{{ _("Manifestation") }}</th>
				</tr>
				<tbody>
					{%- for row in doc.allergies -%}
					<tr>
						<td style="width: 100px;">{{ row.patient_allergies or '' }}</td>
						<td style="width: 200px;">{{ row.manifestation or '' }}</td>
					</tr>
					{% endfor %}
				</tbody>
			</table>
		</div>
		<br>
		{% endif %}
	</div>
	{% endif %}
		{% if doc.patient_addictions|length != 0 %}
		<div class="row">
			<div class="col-xs-12">
			<div><h4>{{ _("Addictions") }}</h4></div>
			<table class="table table-condensed">
				<tr>
					<th>{{ _("Patient Addictions") }}</th>
					<th>{{ _("Consumption") }}</th>
					<th> {{ _("Remarks") }}</th>
				</tr>
				<tbody>
					{%- for row in doc.patient_addictions -%}
					<tr>
						<td style="width: 100px;">{{ row.patient_addictions or '' }}</td>
						<td style="width: 200px;">{{ row.consumption or '' }}</td>
						<td style="width: 200px;">{{ row.remarks or '' }}</td>
					</tr>
					{% endfor %}
				</tbody>
			</table>
		</div>
		</div>
		<br>
		{% endif %}
		{% if doc.surgical_interventions|length != 0 %}
		<div class="row">
			<div class="col-xs-12">
			<div><h4>{{ _("Surgical Background") }}</h4></div>
			<table class="table table-condensed">
				<tr>
					<th>{{ _("Date") }}</th>
					<th>{{ _("Surgical Intervention") }}</th>
					<th> {{ _("Anesthesia Type") }}</th>
					<th> {{ _("Comments") }}</th>
				</tr>
				<tbody>
					{%- for row in doc.surgical_interventions -%}
					<tr>
						<td style="width: 20px;">{{ row.intervention_date or '' }}</td>
						<td style="width: 200px;">{{ row.surgical_intervention or '' }}</td>
						<td style="width: 200px;">{{ row.anesthesia_type or '' }}</td>
						<td style="width: 200px;">{{ row.comments or '' }}</td>
					</tr>
					{% endfor %}
				</tbody>
			</table>
		</div>
		</div>
		<br>
		{% endif %}
{% if doc.first_menses or doc.regular_cycles or doc.cervical_smear or doc.contraception or doc.contraception_end_date or doc.genital_herpes_details or doc.assistant_medical_procreation_details or doc.additional_comments %}
<div><h4>{{ _("Gynecological Background") }}</h4></div>
<div class="row data-field">
 {% if doc.first_menses %}
	<div class="col-xs-4">
		<label>{{ _("First Menses (Year)") }}</label>
		{{ doc.first_menses }}</div>
	{% endif %}
	{% if doc.regular_cycles %}
	<div class="col-xs-4">
		<label>{{ _("Regular Cycles") }}</label>
		{{ doc.regular_cycles }}</div>
	{% endif %}
	{% if doc.cervical_smear %}
	<div class="col-xs-4">
		<label>{{ _("Cervical Smear") }}</label>
		{{ doc.cervical_smear }}</div>
	{% endif %}
	{% if doc.contraception %}
	<div class="col-xs-4">
		<label>{{ _("Contraception") }}</label>
		{{ doc.contraception }}</div>
	{% endif %}
	{% if doc.contraception_end_date %}
	<div class="col-xs-4">
		<label>{{ _("Contraception End Date") }}</label>
		{{ doc.contraception_end_date }}</div>
	{% endif %}
	{% if doc.genital_herpes_details %}
	<div class="col-xs-4">
		<label>{{ _("Genital Herpes") }}</label>
		{{ doc.genital_herpes_details }}</div>
	{% endif %}
	{% if doc.assistant_medical_procreation_details %}
	<div class="col-xs-8">
		<label>{{ _("A.M.P") }}</label>
		{{ doc.assistant_medical_procreation_details }}</div>
	{% endif %}
	{% if doc.additional_comments %}
	<div class="col-xs-8">
		<label>{{ _("Additional Comments") }}</label>
		{{ doc.additional_comments }}</div>
	{% endif %}
</div>
<br>
{% endif %}
{% if doc.parity or doc.gravidity or doc.obstetrical_backgounds|length != 0 %}
<div><h4>{{ _("Obstetrical Background") }}</h4></div>
<div class="row data-field">
 {% if doc.parity %}
	<div class="col-xs-4">
		<label>{{ _("Parity") }}</label>
		{{ doc.parity }}</div>
	{% endif %}
	{% if doc.gravidity %}
	<div class="col-xs-4">
		<label>{{ _("Gravidity") }}</label>
		{{ doc.gravidity }}</div>
	{% endif %}
</div>
	{% if doc.obstetrical_backgounds|length != 0 %}
	<div class="row">
		<div class="col-xs-12">
		<table class="table table-condensed">
			<tbody>
			<tr>
				<th>{{ _("Date") }}</th>
				{%- for row in doc.obstetrical_backgounds -%}
				<td>{{ row.get_formatted("date") or '' }}</td>
				{% endfor %}
			</tr>
			<tr>
				<th>{{ _("Maternity") }}</th>
				{%- for row in doc.obstetrical_backgounds -%}
				<td>{{ row.maternity or '' }}</td>
				{% endfor %}
			</tr>
			<tr>
				<th> {{ _("Term") }}</th>
				{%- for row in doc.obstetrical_backgounds -%}
				<td>{{ row.term or '' }}</td>
				{% endfor %}
			</tr>
			<tr>
				<th> {{ _("Delivery Way") }}</th>
				{%- for row in doc.obstetrical_backgounds -%}
				<td>{{ row.delivery_way }}</td>
				{% endfor %}
			</tr>
			<tr>
				<th> {{ _("Anesthesia") }}</th>
				{%- for row in doc.obstetrical_backgounds -%}
				<td>{{ row.anesthesia or '' }}</td>
				{% endfor %}
			</tr>
			<tr>
				<th> {{ _("Suture") }}</th>
				{%- for row in doc.obstetrical_backgounds -%}
				<td>{{ row.suture or '' }}</td>
				{% endfor %}
			</tr>
			<tr>
				<th> {{ _("Complications") }}</th>
				{%- for row in doc.obstetrical_backgounds -%}
				<td>{{ row.complications or '' }}</td>
				{% endfor %}
			</tr>
			<tr>
				<th> {{ _("Gender") }}</th>
				{%- for row in doc.obstetrical_backgounds -%}
				<td>{{ _(row.child_gender) or '' }}</td>
				{% endfor %}
			</tr>
			<tr>
				<th> {{ _("Weight") }}</th>
				{%- for row in doc.obstetrical_backgounds -%}
				<td>{{ row.child_weight or '' }}</td>
				{% endfor %}
			</tr>
			<tr>
				<th> {{ _("Health State") }}</th>
				{%- for row in doc.obstetrical_backgounds -%}
				<td>{{ row.child_health_state or '' }}</td>
				{% endfor %}
			</tr>
			<tr>
				<th> {{ _("Feeding") }}</th>
				{%- for row in doc.obstetrical_backgounds -%}
				<td>{{ row.feeding }}</td>
				{% endfor %}
			</tr>
			<tr>
				<th> {{ _("Mother Experience") }}</th>
				{%- for row in doc.obstetrical_backgounds -%}
				<td>{{ row.mother_experience or '' }}</td>
				{% endfor %}
			</tr>
			<tr>
				<th> {{ _("Perineum Rehabilitation") }}</th>
				{%- for row in doc.obstetrical_backgounds -%}
				<td>{{ row.perineum_rehabilitation or '' }}</td>
				{% endfor %}
			</tr>
			</tbody>
		</table>
	</div>
	</div>
	<br>
	{% endif %}
{% endif %}
{% if doc.psychatric_background_details %}
 <div class="row data-field">
	 <div class="col-xs-12">
		 <label>{{ _("Psychatric Background") }}</label>
		 {{ doc.psychatric_background_details }}
	 </div>
 </div>
{% endif %}
{% if doc.height or doc.weight or doc.body_mass_index %}
 <div class="row data-field">
	 {% if doc.height %}
	 <div class="col-xs-4">
		 <label>{{ _("Height (Meters)") }}</label>
		 {{ doc.height }}
	 </div>
	 {% endif %}
	 {% if doc.weight %}
	 <div class="col-xs-4">
		 <label>{{ _("Weight (Kg)") }}</label>
		 {{ doc.weight }}
	 </div>
	 {% endif %}
	 {% if doc.body_mass_index %}
	 <div class="col-xs-4">
		 <label>{{ _("Body Mass Index") }}</label>
		 {{ doc.body_mass_index }}
	 </div>
	 {% endif %}
 </div>
 <br>
{% endif %}
{% if doc.mother_background or doc.father_background %}
<div><h4>{{ _("Family Background") }}</h4></div>
 <div class="row data-field">
	 {% if doc.mother_background %}
	 <div class="col-xs-4">
		 <label>{{ _("Mother") }}</label>
		 {{ doc.mother_background }}
	 </div>
	 {% endif %}
	 {% if doc.father_background %}
	 <div class="col-xs-4">
		 <label>{{ _("Father") }}</label>
		 {{ doc.father_background }}
	 </div>
	 {% endif %}
 </div>
 <br>
{% endif %}
{% if doc.spouse_name or doc.spouse_date_of_birth or doc.spouse_age or doc.telephone or doc.spouse_profession or doc.family_situation_details or doc.antecedent_history or doc.spouse_addictions|length != 0 %}
<div><h4>{{ _("Spouse") }}</h4></div>
 <div class="row data-field">
	 {% if doc.spouse_name %}
	 <div class="col-xs-4">
		 <label>{{ _("Full Name") }}</label>
		 {{ doc.spouse_name }}
	 </div>
	 {% endif %}
	 {% if doc.spouse_date_of_birth %}
	 <div class="col-xs-4">
		 <label>{{ _("Date of Birth") }}</label>
		 {{ doc.spouse_date_of_birth }}
	 </div>
	 {% endif %}
	 {% if doc.spouse_age %}
	 <div class="col-xs-4">
		 <label>{{ _("Age") }}</label>
		 {{ doc.spouse_age }}
	 </div>
	 {% endif %}
	 {% if doc.telephone %}
	 <div class="col-xs-4">
		 <label>{{ _("Telephone") }}</label>
		 {{ doc.telephone }}
	 </div>
	 {% endif %}
	 {% if doc.spouse_profession %}
	 <div class="col-xs-4">
		 <label>{{ _("Profession") }}</label>
		 {{ doc.spouse_profession }}
	 </div>
	 {% endif %}
	 {% if doc.family_situation_details %}
	 <div class="col-xs-4">
		 <label>{{ _("Family Situation") }}</label>
		 {{ doc.family_situation_details }}
	 </div>
	 {% endif %}
	 {% if doc.antecedent_history %}
	 <div class="col-xs-12">
		 <label>{{ _("Antecedent History") }}</label>
		 {{ doc.antecedent_history }}
	 </div>
	 {% endif %}
	 {% if doc.spouse_addictions|length != 0 %}
		 <div class="col-xs-12">
		 <table class="table table-condensed">
			 <tr>
				 <th>{{ _("Spouse Addictions") }}</th>
				 <th>{{ _("Consumption") }}</th>
				 <th> {{ _("Remarks") }}</th>
			 </tr>
			 <tbody>
				 {%- for row in doc.spouse_addictions -%}
				 <tr>
					 <td style="width: 100px;">{{ row.spouse_addictions }}</td>
					 <td style="width: 200px;">{{ row.consumption }}</td>
					 <td style="width: 200px;">{{ row.remarks }}</td>
				 </tr>
				 {% endfor %}
			 </tbody>
		 </table>
	 </div>
	 <br>
	 {% endif %}
 </div>
{% endif %}
</div>
