css:
.col-xs-4, .col-xs-6, .col-xs-8, .col-xs-12 {
	padding-top: 15px;
}

table, tr, td, th, tbody, thead, tfoot, .col-xs-4, .col-xs-6, .col-xs-8, .col-xs-12, section {
	page-break-inside: avoid !important;
}

h1, h2, h3, h4, h5, h6, p {
	color: #434d56 !important;
}

label, div {
	color: #4f5a64 !important;
}

.print-heading {
	margin: 0.4in !important;
	text-align: left;
	text-transform: uppercase;
	padding-bottom: 20px;
	margin-bottom: 20px;
	border-bottom: 1px solid #d1d8dd;
}

.print-format {
	padding: 0;
	border-radius: 5px;
	margin: auto;
	position: relative;
}

.print-format th {
	background-color: #fff !important;
}

.header-html {
	padding: 0.4in;
	background: #f5fafc !important;
	border-radius: 5px 5px 0 0;
	width: 100%;
}

.body-html {
	padding: 0 0.8in 0.8in 0.8in;
}

.table > tbody + tbody {
	border-top: 1px solid #d1d8dd;
}

.table > tbody > tr > th {
	border-bottom: 1px solid #d1d8dd !important;
}

.table > tbody > tr > td {
	border-bottom: 1px solid #d1d8dd;
}

.vertical-table > tbody > tr > th {
	border-right: 1px solid #d1d8dd !important;
	width: 100px;
}

@media print {
	h1, h2, h3, h4, h5, h6, p, th, td, strong, a, b, ul, li {
		color: #434d56 !important;
	}
	.print-heading {
		margin: 0.4in !important;
		color: #657381 !important;
	}
	.header-html {
		background: #f5fafc !important;
	}
	.body-html {
		padding: 0 0.4in 0.4in 0.4in !important;
	}
	.letter-head-footer {
		padding-top: 20px;
	}
	a[href]:after {
		content: none;
	}
}

html:
{% include "templates/includes/folder_header.html" %}

<div class="body-html">
	<section>
		{% if doc.consultation_purpose %}
		<div>
			<h4>{{ _("Consultation Purpose") }}</h4>
		</div>
		<div class="row data-field">
			{% if doc.consultation_purpose %}
			<div class="col-xs-12">
				{{ doc.consultation_purpose }}</div>
			{% endif %}
		</div>
		{% endif %}
	</section>
	<section>
		{% if doc.before_delivery_bearing_down==1 or doc.urinary_stress_incontinence==1 or doc.weight_gain!=0 %}
		<div>
			<h4>{{ _("Before Delivery") }}</h4>
		</div>
		<div class="row data-field">
			{% if doc.before_delivery_bearing_down==1 %}
			<div class="col-xs-4">
				<label>{{ _("Bearing Down") }}</label> {{ _("Yes") }}</div>
			{% endif %} {% if doc.urinary_stress_incontinence==1 %}
			<div class="col-xs-4">
				<label>{{ _("Urinary Stress Incontinence") }}</label> {{ doc.which_term }} {{ _("Amenorrhea Weeks") }}</div>
			{% endif %} {% if doc.weight_gain!=0 %}
			<div class="col-xs-6">
				<label>{{ _("Weight Gain") }}</label> {{ doc.weight_gain }} Kg</div>
			{% endif %}
		</div>
		<br> {% endif %} {% if doc.vulval_apertognathia==1 or doc.vulval_air_bubble==1 or doc.lapping_during_walking==1 or
		doc.vaginal_gas==1 or doc.loss_of_water_after_bath==1 or doc.vulval_area_other %}
		<div>
			<h4>{{ _("After Delivery") }}</h4>
		</div>
		<div>
			<h5>{{ _("Vulval Area") }}</h5>
		</div>
		<div class="row data-field">
			{% if doc.vulval_apertognathia==1 %}
			<div class="col-xs-4">
				<label>{{ _("Vuval Apertognathia ?") }}</label> {{ _("Yes") }}</div>
			{% endif %} {% if doc.vulval_air_bubble==1 %}
			<div class="col-xs-4">
				<label>{{ _("Vulval Air Bubble ?") }}</label> {{ _("Yes") }}</div>
			{% endif %} {% if doc.lapping_during_walking==1 %}
			<div class="col-xs-4">
				<label>{{ _("Lapping During Walking ?") }}</label> {{ _("Yes") }}</div>
			{% endif %} {% if doc.vaginal_gas==1 %}
			<div class="col-xs-4">
				<label>{{ _("Vaginal Gas ?") }}</label> {{ _("Yes") }}</div>
			{% endif %} {% if doc.loss_of_water_after_bath==1 %}
			<div class="col-xs-4">
				<label>{{ _("Loss of Water after Bath ?") }}</label> {{ _("Yes") }}</div>
			{% endif %} {% if doc.vulval_area_other %}
			<div class="col-xs-4">
				<label>{{ _("Other") }}</label> {{ doc.get_formatted("vulval_area_other") }}</div>
			{% endif %}
		</div>
		<br> {% endif %}
	</section>
	<section>
		{% if doc.after_delivery_bearing_down==1 or doc.resumption_of_sexual_activity or doc.contraception_desired or
		doc.postpartum_return_of_menses or doc.vaginal_area_other %}
		<div>
			<h5>{{ _("Vaginal Area") }}</h5>
		</div>
		<div class="row data-field">
			{% if doc.after_delivery_bearing_down==1 %}
			<div class="col-xs-4">
				<label>{{ _("Bearing Down ?") }}</label> {{ doc.bearing_down_circumstances }}</div>
			{% endif %} {% if doc.resumption_of_sexual_activity %}
			<div class="col-xs-4">
				<label>{{ _("Resumption of Sexual Activity ?") }}</label> {{ _("Yes") }}</div>
			{% if doc.pain_rating %}
			<div class="col-xs-4">
				<label>{{ _("Pain Rating") }}</label> {{ doc.pain_rating }}</div>
			{% endif %} {% if doc.satisfaction %}
			<div class="col-xs-4">
				<label>{{ _("Satisfaction") }}</label> {{ doc.satisfaction }}
			</div>
			{% endif %} {% endif %} {% if doc.contraception_desired %}
			<div class="col-xs-4">
				<label>{{ _("Contraception Desired") }}</label> {{ doc.contraception_desired }}</div>
			{% endif %} {% if doc.postpartum_return_of_menses %}
			<div class="col-xs-4">
				<label>{{ _("Postpartum Return of Menses") }}</label> {{ doc.postpartum_return_of_menses }}</div>
			{% endif %} {% if doc.vaginal_area_other %}
			<div class="col-xs-4">
				<label>{{ _("Other") }}</label> {{ doc.get_formatted("vaginal_area_other") }}</div>
			{% endif %}
		</div>
		<br> {% endif %}
	</section>
	<section>
		{% if doc.water_consumption!=0 or doc.tea_coffee_consumption!=0 or doc.number_of_urination!=0 or doc.pollakiuria or
		doc.nycturia or doc.stop_test or doc.anticipation_urination or doc.urgency_of_urination or doc.overactive_bladder or
		doc.dysuria
		or doc.urinary_area_other %}
		<div>
			<h5>{{ _("Urinary Area") }}</h5>
		</div>
		<div class="row data-field">
			{% if doc.water_consumption %}
			<div class="col-xs-4">
				<label>{{ _("Water Consumption/24h") }}</label> {{ doc.water_consumption or "" }}</div>
			{% endif %} {% if doc.tea_coffee_consumption %}
			<div class="col-xs-4">
				<label>{{ _("Tea/Coffee Consumtion/24h") }}</label> {{ doc.tea_coffee_consumption or "" }}</div>
			{% endif %} {% if doc.number_of_urination!=0 %}
			<div class="col-xs-4">
				<label>{{ _("Number of Urination/24h") }}</label> {{ doc.number_of_urination or "" }}</div>
			{% endif %} {% if doc.pollakiuria %}
			<div class="col-xs-4">
				<label>{{ _("Pollakiuria") }}</label> {{ _("Yes") }}</div>
			{% endif %} {% if doc.nycturia %}
			<div class="col-xs-4">
				<label>{{ _("Nycturia (Number of Times)") }}</label> {{ doc.nycturia }}</div>
			{% endif %} {% if doc.stop_test %}
			<div class="col-xs-4">
				<label>{{ _("Urination Stop Test") }}</label> {{ _("Yes") }}</div>
			{% endif %} {% if doc.anticipation_urination %}
			<div class="col-xs-4">
				<label>{{ _("Anticipation Urination ?") }}</label> {{ _("Yes") }}</div>
			{% endif %} {% if doc.urgency_of_urination %}
			<div class="col-xs-4">
				<label>{{ _("Urgency of Urination or Stress Incontinence") }}</label> {{ doc.urgency_of_urination }}<br>{{
				doc.stress_incontinence_circumpstances or "" }}</div>
			{% endif %} {% if doc.overactive_bladder %}
			<div class="col-xs-4">
				<label>{{ _("Overactive Bladder") }}</label> {{ doc.overactive_bladder }}</div>
			{% endif %} {% if doc.dysuria %}
			<div class="col-xs-4">
				<label>{{ _("Dysuria (Details)") }}</label> {{ doc.get_formatted("dysuria") }}</div>
			{% endif %} {% if doc.urinary_area_other %}
			<div class="col-xs-4">
				<label>{{ _("Other") }}</label> {{ doc.get_formatted("urinary_area_other") }}</div>
			{% endif %}
		</div>
		<br> {% endif %}
	</section>
	<section>
		{% if doc.transit_since_delivery or doc.anal_incontinence or doc.urge or doc.dyschesia or doc.haemorrhoid or
		doc.anal_area_other or doc.pelvis_statis or doc.resumption_of_sport %}
		<div>
			<h5>{{ _("Anal Area") }}</h5>
		</div>
		<div class="row data-field">
			{% if doc.transit_since_delivery %}
			<div class="col-xs-4">
				<label>{{ _("Transit Since Delivery") }}</label> {{ doc.transit_since_delivery }}</div>
			{% endif %} {% if doc.anal_incontinence %}
			<div class="col-xs-4">
				<label>{{ _("Anal Incontinence") }}</label> {{ doc.anal_incontinence }}</div>
			{% endif %} {% if doc.urge %}
			<div class="col-xs-4">
				<label>{{ _("Urge ?") }}</label> {{ doc.urge }}</div>
			{% endif %} {% if doc.dyschesia %}
			<div class="col-xs-4">
				<label>{{ _("Dyschesia") }}</label> {{ doc.dyschesia }}</div>
			{% endif %} {% if doc.haemorrhoid %}
			<div class="col-xs-4">
				<label>{{ _("Haemorrhoid") }}</label> {{ doc.haemorrhoid }}</div>
			{% endif %} {% if doc.anal_area_other %}
			<div class="col-xs-6">
				<label>{{ _("Other") }}</label> {{ doc.get_formatted("anal_area_other") }}</div>
			{% endif %} {% if doc.pelvis_statis %}
			<div class="col-xs-4">
				<label>{{ _("Pelvis Statics") }}</label> {{ doc.pelvis_statis }}</div>
			{% endif %} {% if doc.resumption_of_sport %}
			<div class="col-xs-4">
				<label>{{ _("Resumption of Sporting Activities") }}</label> {{ doc.resumption_of_sport }}</div>
			{% endif %}
		</div>
		<br> {% endif %}
	</section>
	<section>
		{% if doc.diasthasis==1 or doc.physical_aperthognathia or doc.scar or doc.vaginal_bands or doc.bladder_position or
		doc.urethra_adherence %}
		<div>
			<h4>{{ _("Physical Examination") }}</h4>
		</div>
		<div>
			<h5>{{ _("Vulva") }}</h5>
		</div>
		<div class="row data-field">
			{% if doc.diasthasis==1 %}
			<div class="col-xs-4">
				<label>{{ _("Diasthasis ?") }}</label> {{ _("Yes") }}</div>
			{% endif %} {% if doc.physical_aperthognathia %}
			<div class="col-xs-4">
				<label>{{ _("Aperthognathia") }}</label> {{ doc.physical_aperthognathia }}</div>
			{% endif %} {% if doc.scar %}
			<div class="col-xs-4">
				<label>{{ _("Scar") }}</label> {{ doc.scar }}</div>
			{% endif %} {% if doc.vaginal_bands %}
			<div class="col-xs-4">
				<label>{{ _("Vaginal Bands") }}</label> {{ doc.vaginal_bands }}</div>
			{% endif %} {% if doc.bladder_position %}
			<div class="col-xs-4">
				<label>{{ _("Bladder Position") }}</label> {{ doc.bladder_position }}</div>
			{% endif %} {% if doc.urethra_adherence %}
			<div class="col-xs-4">
				<label>{{ _("Urethra Adherence ?") }}</label> {{ _("Yes") }}</div>
			{% endif %}
		</div>
		{% endif %}
	</section>
	<section>
		{% if doc.fundus_apertognathia or doc.testing or doc.contraction_duration or doc.cough_test or doc.command_inversion
		%}
		<div>
			<h5>{{ _("Testing") }}</h5>
		</div>
		<div class="row data-field">
			{% if doc.fundus_apertognathia %}
			<div class="col-xs-4">
				<label>{{ _("Fundus Apertognathia") }}</label> {{ doc.fundus_apertognathia }}</div>
			{% endif %} {% if doc.testing %}
			<div class="col-xs-4">
				<label>{{ _("Testing") }}</label> {{ doc.testing }}</div>
			{% endif %} {% if doc.contraction_duration %}
			<div class="col-xs-4">
				<label>{{ _("Contraction Duration") }}</label> {{ doc.contraction_duration }}</div>
			{% endif %} {% if doc.cough_test %}
			<div class="col-xs-4">
				<label>{{ _("Cough Test") }}</label> {{ doc.cough_test }}</div>
			{% endif %} {% if doc.command_inversion %}
			<div class="col-xs-4">
				<label>{{ _("Command Inversion ?") }}</label> {{ _("Yes") }}</div>
			{% endif %}
		</div>
		<br> {% endif %}
	</section>
	<section>
		{% if doc.vaginal_walls or doc.anterior_contraction or doc.posterior_contraction or doc.left_contraction or
		doc.right_contraction %}
		<div>
			<h5>{{ _("Tonus") }}</h5>
		</div>
		<div class="row data-field">
			{% if doc.vaginal_walls %}
			<div class="col-xs-4">
				<label>{{ _("Vaginal Walls") }}</label> {{ doc.vaginal_walls }}</div>
			{% endif %} {% if doc.anterior_contraction %}
			<div class="col-xs-4">
				<label>{{ _("Anterior Contraction") }}</label> {{ doc.anterior_contraction }}</div>
			{% endif %} {% if doc.posterior_contraction %}
			<div class="col-xs-4">
				<label>{{ _("Posterior Contraction") }}</label> {{ doc.posterior_contraction }}</div>
			{% endif %} {% if doc.left_contraction %}
			<div class="col-xs-4">
				<label>{{ _("Left Contraction") }}</label> {{ doc.left_contraction }}</div>
			{% endif %} {% if doc.right_contraction %}
			<div class="col-xs-4">
				<label>{{ _("Right Contraction") }}</label> {{ doc.right_contraction }}</div>
			{% endif %}
		</div>
		<br> {% endif %}
	</section>

	<div id="footer-html" class="visible-pdf">
		{% if not no_letterhead and footer %}
		<div class="letter-head-footer">
			{{ footer }}
		</div>
		{% endif %}
		<p class="text-center small page-number visible-pdf">
			{{ _("Page {0} of {1}").format('<span class="page"></span>', '<span class="topage"></span>') }}
		</p>
	</div>
</div>
