css:
.col-xs-4, .col-xs-6, .col-xs-8, .col-xs-12 {
  padding-top: 15px;
}

table, tr, td, th, tbody, thead, tfoot, .col-xs-4, .col-xs-6, .col-xs-8, .col-xs-12 {
    page-break-inside: avoid !important;
}

html:
{% include "templates/includes/folder_header.html" %}

<div>
	<div id="footer-html" class="visible-pdf">
		{% if not no_letterhead and footer %}
		<div class="letter-head-footer">
			{{ footer }}
		</div>
		{% endif %}
		<p class="text-center small page-number visible-pdf">
			{{ _("Page {0} of {1}").format('<span class="page"></span>', '<span class="topage"></span>') }}
		</p>
	</div>

 {% if doc.weight or doc.edema or doc.urinary_functional_signs or doc.bmi or doc.asthenia or doc.high_blood_pressure_signs or doc.blood_pressure %}
 	<div><h4>{{ _("Clinical Exam") }}</h4></div>
	<div class="row data-field">
		{% if doc.weight %}
		<div class="col-xs-4">
			<label>{{ _("Weight") }}</label>
			{{ doc.weight }}</div>
		{% endif %}
		{% if doc.edema %}
		<div class="col-xs-4">
			<label>{{ _("Edema") }}</label>
			{{ doc.edema }}</div>
		{% endif %}
		{% if doc.urinary_functional_signs %}
		<div class="col-xs-4">
			<label>{{ _("Urinary Functional Signs") }}</label>
			{{ doc.urinary_functional_signs }}</div>
		{% endif %}
		{% if doc.bmi %}
		<div class="col-xs-4">
			<label>{{ _("BMI") }}</label>
			{{ doc.bmi }}</div>
		{% endif %}
		{% if doc.asthenia %}
		<div class="col-xs-4">
			<label>{{ _("Asthenia") }}</label>
			{{ doc.asthenia }}</div>
		{% endif %}
		{% if doc.high_blood_pressure_signs %}
		<div class="col-xs-4">
			<label>{{ _("High Blood Pressure Signs") }}</label>
			{{ doc.high_blood_pressure_signs }}</div>
		{% endif %}
		{% if doc.blood_pressure %}
		<div class="col-xs-4">
			<label>{{ _("Blood Pressure") }}</label>
			{{ doc.blood_pressure }}</div>
		{% endif %}
	</div>
	<br>
	{% endif %}
	{% if doc.resumption_of_menses or doc.breasts or doc.contraception or doc.vulva or doc.sexuality or doc.vaginal_exam or doc.cervical_smear %}
	<div><h4>{{ _("Gynecological Exam") }}</h4></div>
 	<div class="row data-field">
 		{% if doc.resumption_of_menses %}
 		<div class="col-xs-4">
 			<label>{{ _("Resumption of Menses") }}</label>
 			{{ doc.resumption_of_menses }}</div>
 		{% endif %}
 		{% if doc.breasts %}
 		<div class="col-xs-4">
 			<label>{{ _("Breasts") }}</label>
 			{{ doc.breasts }}</div>
 		{% endif %}
		{% if doc.contraception %}
		<div class="col-xs-4">
			<label>{{ _("Contraception") }}</label>
			{{ doc.contraception }}</div>
		{% endif %}
		{% if doc.vulva %}
		<div class="col-xs-4">
			<label>{{ _("Vulva") }}</label>
			{{ doc.vulva }}</div>
		{% endif %}
		{% if doc.sexuality %}
		<div class="col-xs-4">
			<label>{{ _("Sexuality") }}</label>
			{{ doc.sexuality }}</div>
		{% endif %}
		{% if doc.vaginal_exam %}
		<div class="col-xs-4">
			<label>{{ _("Vaginal Exam") }}</label>
			{{ doc.vaginal_exam }}</div>
		{% endif %}
		{% if doc.cervical_smear %}
		<div class="col-xs-4">
			<label>{{ _("Cervical Smear") }}</label>
			{{ doc.cervical_smear }}</div>
		{% endif %}
	</div>
 	<br>
 	{% endif %}
	{% if doc.comments %}
	<div><h4>{{ _("Comments") }}</h4></div>
	<div class="row data-field">
		<div class="col-xs-12">
			{{ doc.get_formatted("comments") }}</div>
	</div>
	<br>
	{% endif %}
</div>
