css:
.col-xs-4, .col-xs-6, .col-xs-8, .col-xs-12 {
	padding-top: 15px;
}

table, tr, td, th, tbody, thead, tfoot, .col-xs-4, .col-xs-6, .col-xs-8, .col-xs-12, section {
	page-break-inside: avoid !important;
}

h1, h2, h3, h4, h5, h6, p {
	color: #434d56 !important;
}

label, div {
	color: #4f5a64 !important;
}

.print-heading {
	margin: 0.4in !important;
	text-align: left;
	text-transform: uppercase;
	padding-bottom: 20px;
	margin-bottom: 20px;
	border-bottom: 1px solid #d1d8dd;
}

.print-format {
	padding: 0;
	border-radius: 5px;
	margin: auto;
	position: relative;
}

.print-format th {
	background-color: #fff !important;
}

.header-html {
	padding: 0.4in;
	background: #f5fafc !important;
	border-radius: 5px 5px 0 0;
	width: 100%;
}

.body-html {
	padding: 0 0.8in 0.8in 0.8in;
}

.table > tbody + tbody {
	border-top: 1px solid #d1d8dd;
}

.table > tbody > tr > th {
	border-bottom: 1px solid #d1d8dd !important;
}

.table > tbody > tr > td {
	border-bottom: 1px solid #d1d8dd;
}

.vertical-table > tbody > tr > th {
	border-right: 1px solid #d1d8dd !important;
	width: 100px;
}

@media print {
	h1, h2, h3, h4, h5, h6, p, th, td, strong, a, b, ul, li {
		color: #434d56 !important;
	}
	.print-format {
	    padding: 0 25px;
	}
	.print-heading {
		margin-top: 25mm;
		margin-bottom: 25mm;
		color: #657381 !important;
	}
	.header-html {
		background: #f5fafc !important;
	}
	.body-html {
		padding: 0 0.4in 0.4in 0.4in !important;
	}
	.letter-head-footer {
		padding-top: 20px;
	}
	a[href]:after {
		content: none;
	}
}

html:
{% include "templates/includes/folder_header.html" %}

<div class="body-html">

	<section>
		{% if doc.weight or doc.edema or doc.urinary_functional_signs or doc.bmi or doc.asthenia or
		doc.high_blood_pressure_signs or doc.blood_pressure %}
		<div>
			<h4>{{ _("Clinical Exam") }}</h4>
		</div>
		<div class="row data-field">
			{% if doc.weight %}
			<div class="col-xs-4">
				<label>{{ _("Weight") }}</label>
				{{ doc.weight }}</div>
			{% endif %}
			{% if doc.edema %}
			<div class="col-xs-4">
				<label>{{ _("Edema") }}</label>
				{{ doc.edema }}</div>
			{% endif %}
			{% if doc.urinary_functional_signs %}
			<div class="col-xs-4">
				<label>{{ _("Urinary Functional Signs") }}</label>
				{{ doc.urinary_functional_signs }}</div>
			{% endif %}
			{% if doc.bmi %}
			<div class="col-xs-4">
				<label>{{ _("BMI") }}</label>
				{{ doc.bmi }}</div>
			{% endif %}
			{% if doc.asthenia %}
			<div class="col-xs-4">
				<label>{{ _("Asthenia") }}</label>
				{{ doc.asthenia }}</div>
			{% endif %}
			{% if doc.high_blood_pressure_signs %}
			<div class="col-xs-4">
				<label>{{ _("High Blood Pressure Signs") }}</label>
				{{ doc.high_blood_pressure_signs }}</div>
			{% endif %}
			{% if doc.blood_pressure %}
			<div class="col-xs-4">
				<label>{{ _("Blood Pressure") }}</label>
				{{ doc.blood_pressure }}</div>
			{% endif %}
		</div>
		<br>
		{% endif %}
	</section>
	<section>
		{% if doc.resumption_of_menses or doc.breasts or doc.contraception or doc.vulva or doc.sexuality or doc.vaginal_exam
		or doc.cervical_smear %}
		<div>
			<h4>{{ _("Gynecological Exam") }}</h4>
		</div>
		<div class="row data-field">
			{% if doc.resumption_of_menses %}
			<div class="col-xs-4">
				<label>{{ _("Resumption of Menses") }}</label>
				{{ doc.resumption_of_menses }}</div>
			{% endif %}
			{% if doc.breasts %}
			<div class="col-xs-4">
				<label>{{ _("Breasts") }}</label>
				{{ doc.breasts }}</div>
			{% endif %}
			{% if doc.contraception %}
			<div class="col-xs-4">
				<label>{{ _("Contraception") }}</label>
				{{ doc.contraception }}</div>
			{% endif %}
			{% if doc.vulva %}
			<div class="col-xs-4">
				<label>{{ _("Vulva") }}</label>
				{{ doc.vulva }}</div>
			{% endif %}
			{% if doc.sexuality %}
			<div class="col-xs-4">
				<label>{{ _("Sexuality") }}</label>
				{{ doc.sexuality }}</div>
			{% endif %}
			{% if doc.vaginal_exam %}
			<div class="col-xs-4">
				<label>{{ _("Vaginal Exam") }}</label>
				{{ doc.vaginal_exam }}</div>
			{% endif %}
			{% if doc.cervical_smear %}
			<div class="col-xs-4">
				<label>{{ _("Cervical Smear") }}</label>
				{{ doc.cervical_smear }}</div>
			{% endif %}
		</div>
		<br>
		{% endif %}
	</section>
	<section>
		{% if doc.comments %}
		<div>
			<h4>{{ _("Comments") }}</h4>
		</div>
		<div class="row data-field">
			<div class="col-xs-12">
				{{ doc.get_formatted("comments") }}</div>
		</div>
		<br>
		{% endif %}
	</section>

	<div id="footer-html" class="visible-pdf">
		{% if not no_letterhead and footer %}
		<div class="letter-head-footer">
			{{ footer }}
		</div>
		{% endif %}
		<p class="text-center small page-number visible-pdf">
			{{ _("Page {0} of {1}").format('<span class="page"></span>', '<span class="topage"></span>') }}
		</p>
	</div>
</div>
