css:
.col-xs-4, .col-xs-6, .col-xs-8, .col-xs-12 {
  padding-top: 15px;
}

table, tr, td, th, tbody, thead, tfoot {
    page-break-inside: avoid !important;
}

html:
{% include "templates/includes/folder_header.html" %}

<div>
	<div id="footer-html" class="visible-pdf">
		{% if not no_letterhead and footer %}
		<div class="letter-head-footer">
			{{ footer }}
		</div>
		{% endif %}
		<p class="text-center small page-number visible-pdf">
			{{ _("Page {0} of {1}").format('<span class="page"></span>', '<span class="topage"></span>') }}
		</p>
	</div>
	{% if doc.term %}
	<div class="row data-field">
	<div class="col-xs-4">
		<label>{{ _("Term") }}</label>
		{{ doc.term }}</div>
	</div>
	<br>
	{% endif %}

 {% if doc.blood_pressure or doc.pulsations or doc.temperature or doc.general_health %}
	<div><h4>{{ _("General Exam") }}</h4></div>
	<div class="row data-field">
		{% if doc.blood_pressure %}
		<div class="col-xs-4">
			<label>{{ _("Blood Pressure") }}</label>
			{{ doc.blood_pressure }}</div>
		{% endif %}
		{% if doc.pulsations %}
		<div class="col-xs-4">
			<label>{{ _("Pulsations") }}</label>
			{{ doc.pulsations }}</div>
		{% endif %}
		{% if doc.temperature %}
		<div class="col-xs-4">
			<label>{{ _("Temperature") }}</label>
			{{ doc.temperature }}</div>
		{% endif %}
	{% if doc.general_health %}
	<div class="col-xs-4">
		<label>{{ _("General Health") }}</label>
		{{ doc.general_health }}</div>
	{% endif %}
</div>
	<br>
	{% endif %}
	{% if doc.weight or doc.weight_gain %}
 	<div class="row data-field">
 		{% if doc.weight %}
 		<div class="col-xs-4">
 			<label>{{ _("Weight") }}</label>
 			{{ doc.weight }}</div>
 		{% endif %}
 		{% if doc.weight_gain %}
 		<div class="col-xs-4">
 			<label>{{ _("Weight Gain") }}</label>
 			{{ doc.weight_gain }}</div>
 		{% endif %}
	</div>
 	<br>
 	{% endif %}
	{% if doc.functional_signs_of_hypertension or doc.edema %}
 	<div class="row data-field">
 		{% if doc.functional_signs_of_hypertension %}
 		<div class="col-xs-4">
 			<label>{{ _("Functional Signs of Hypertension") }}</label>
 			{{ doc.functional_signs_of_hypertension }}</div>
 		{% endif %}
 		{% if doc.edema %}
 		<div class="col-xs-4">
 			<label>{{ _("Edema") }}</label>
 			{{ doc.edema }}</div>
 		{% endif %}
	</div>
 	<br>
 	{% endif %}
	{% if doc.dipstick or doc.urinary_functional_signs %}
	<div class="row data-field">
		{% if doc.dipstick %}
		<div class="col-xs-4">
			<label>{{ _("Dipstick") }}</label>
			{{ doc.dipstick }}</div>
		{% endif %}
		{% if doc.urinary_functional_signs %}
		<div class="col-xs-4">
			<label>{{ _("Urinary Functional Signs") }}</label>
			{{ doc.urinary_functional_signs }}</div>
		{% endif %}
	</div>
	<br>
	{% endif %}
	{% if doc.foetal_active_movement or doc.uterine_contractions or doc.pain or doc.losses or doc.metrorrhagia or doc.amniotic_liquid %}
 	<div><h4>{{ _("Obstetrical Exam") }}</h4></div>
 	<div class="row data-field">
 		{% if doc.foetal_active_movement %}
 		<div class="col-xs-4">
 			<label>{{ _("Foetal Active Movement") }}</label>
 			{{ doc.foetal_active_movement }}</div>
 		{% endif %}
 		{% if doc.uterine_contractions %}
 		<div class="col-xs-4">
 			<label>{{ _("Uterine Contractions") }}</label>
 			{{ doc.uterine_contractions }}</div>
 		{% endif %}
 		{% if doc.pain %}
 		<div class="col-xs-4">
 			<label>{{ _("Pain") }}</label>
 			{{ doc.pain }}</div>
 		{% endif %}
 	{% if doc.losses %}
 	<div class="col-xs-4">
 		<label>{{ _("Losses") }}</label>
 		{{ doc.losses }}</div>
 	{% endif %}
	 	{% if doc.metrorrhagia %}
	<div class="col-xs-4">
		<label>{{ _("Metrorrhagia") }}</label>
		{{ doc.metrorrhagia }}</div>
	{% endif %}
	{% if doc.amniotic_liquid %}
	<div class="col-xs-4">
		<label>{{ _("Amniotic Liquid") }}</label>
		{{ doc.amniotic_liquid }}</div>
	{% endif %}
 </div>
 	<br>
 	{% endif %}
	{% if doc.uterine_height or doc.heart_noise or doc.foetal_presentation or doc.vaginal_examination %}
	<div class="row data-field">
		{% if doc.uterine_height %}
		<div class="col-xs-4">
			<label>{{ _("Uterine Height") }}</label>
			{{ doc.uterine_height }}</div>
		{% endif %}
		{% if doc.heart_noise %}
		<div class="col-xs-4">
			<label>{{ _("Heart Noise") }}</label>
			{{ doc.heart_noise }}</div>
		{% endif %}
		{% if doc.foetal_presentation %}
		<div class="col-xs-4">
			<label>{{ _("Foetal Presentation") }}</label>
			{{ doc.foetal_presentation }}</div>
		{% endif %}
	{% if doc.vaginal_examination %}
	<div class="col-xs-6">
		<label>{{ _("Vaginal Examination") }}</label>
		{{ doc.vaginal_examination }}</div>
	{% endif %}
 </div>
	<br>
	{% endif %}
	{% if doc.notes %}
	<div><h4>{{ _("Notes") }}</h4></div>
	<div class="row data-field">
		<div class="col-xs-12">
			{{ doc.notes }}</div>
	</div>
	<br>
	{% endif %}
</div>
