css:
.col-xs-4, .col-xs-6, .col-xs-8, .col-xs-12 {
	padding-top: 15px;
}

table, tr, td, th, tbody, thead, tfoot, .col-xs-4, .col-xs-6, .col-xs-8, .col-xs-12, section {
	page-break-inside: avoid !important;
}

h1, h2, h3, h4, h5, h6, p {
	color: #434d56 !important;
}

label, div {
	color: #4f5a64 !important;
}

.print-heading {
	margin: 0.4in !important;
	text-align: left;
	text-transform: uppercase;
	padding-bottom: 20px;
	margin-bottom: 20px;
	border-bottom: 1px solid #d1d8dd;
}

.print-format {
	padding: 0;
	border-radius: 5px;
	margin: auto;
	position: relative;
}

.print-format th {
	background-color: #fff !important;
}

.header-html {
	padding: 0.4in;
	background: #f5fafc !important;
	border-radius: 5px 5px 0 0;
	width: 100%;
}

.body-html {
	padding: 0 0.8in 0.8in 0.8in;
}

.table > tbody + tbody {
	border-top: 1px solid #d1d8dd;
}

.table > tbody > tr > th {
	border-bottom: 1px solid #d1d8dd !important;
}

.table > tbody > tr > td {
	border-bottom: 1px solid #d1d8dd;
}

.vertical-table > tbody > tr > th {
	border-right: 1px solid #d1d8dd !important;
	width: 100px;
}

@media print {
	h1, h2, h3, h4, h5, h6, p, th, td, strong, a, b, ul, li {
		color: #434d56 !important;
	}
	.print-format {
	    padding: 0 25px;
	}
	.print-heading {
		margin: 0.4in !important;
		color: #657381 !important;
	}
	.header-html {
		background: #f5fafc !important;
	}
	.body-html {
		padding: 0 0.4in 0.4in 0.4in !important;
	}
	.letter-head-footer {
		padding-top: 20px;
	}
	a[href]:after {
		content: none;
	}
}

html:
{% include "templates/includes/folder_header.html" %}

<div class="body-html">
	<section>
		{% if doc.term %}
		<div class="row data-field">
			<div class="col-xs-4">
				<label>{{ _("Term") }}</label>
				{{ doc.term }}</div>
		</div>
		<br>
		{% endif %}
	</section>
	<section>
		{% if doc.blood_pressure or doc.pulsations or doc.temperature or doc.general_health %}
		<div>
			<h4>{{ _("General Exam") }}</h4>
		</div>
		<div class="row data-field">
			{% if doc.blood_pressure %}
			<div class="col-xs-4">
				<label>{{ _("Blood Pressure") }}</label>
				{{ doc.blood_pressure }}</div>
			{% endif %}
			{% if doc.pulsations %}
			<div class="col-xs-4">
				<label>{{ _("Pulsations") }}</label>
				{{ doc.pulsations }}</div>
			{% endif %}
			{% if doc.temperature %}
			<div class="col-xs-4">
				<label>{{ _("Temperature") }}</label>
				{{ doc.temperature }}</div>
			{% endif %}
			{% if doc.general_health %}
			<div class="col-xs-4">
				<label>{{ _("General Health") }}</label>
				{{ doc.general_health }}</div>
			{% endif %}
		</div>
		<br>
		{% endif %}
	</section>
	<section>
		{% if doc.weight or doc.weight_gain %}
		<div class="row data-field">
			{% if doc.weight %}
			<div class="col-xs-4">
				<label>{{ _("Weight") }}</label>
				{{ doc.weight }} Kg</div>
			{% endif %}
			{% if doc.weight_gain %}
			<div class="col-xs-4">
				<label>{{ _("Weight Gain") }}</label>
				{{ doc.weight_gain }}</div>
			{% endif %}
		</div>
		<br>
		{% endif %}
	</section>
	<section>
		{% if doc.functional_signs_of_hypertension or doc.edema %}
		<div class="row data-field">
			{% if doc.functional_signs_of_hypertension %}
			<div class="col-xs-4">
				<label>{{ _("Functional Signs of Hypertension") }}</label>
				{{ doc.functional_signs_of_hypertension }}</div>
			{% endif %}
			{% if doc.edema %}
			<div class="col-xs-4">
				<label>{{ _("Edema") }}</label>
				{{ doc.edema }}</div>
			{% endif %}
		</div>
		<br>
		{% endif %}
	</section>
	<section>
		{% if doc.dipstick or doc.urinary_functional_signs %}
		<div class="row data-field">
			{% if doc.dipstick %}
			<div class="col-xs-4">
				<label>{{ _("Dipstick") }}</label>
				{{ doc.dipstick }}</div>
			{% endif %}
			{% if doc.urinary_functional_signs %}
			<div class="col-xs-4">
				<label>{{ _("Urinary Functional Signs") }}</label>
				{{ doc.urinary_functional_signs }}</div>
			{% endif %}
		</div>
		<br>
		{% endif %}
	</section>
	<section>
		{% if doc.foetal_active_movement or doc.uterine_contractions or doc.pain or doc.losses or doc.metrorrhagia or
		doc.amniotic_liquid %}
		<div>
			<h4>{{ _("Obstetrical Exam") }}</h4>
		</div>
		<div class="row data-field">
			{% if doc.foetal_active_movement %}
			<div class="col-xs-4">
				<label>{{ _("Foetal Active Movement") }}</label>
				{{ doc.foetal_active_movement }}</div>
			{% endif %}
			{% if doc.uterine_contractions %}
			<div class="col-xs-4">
				<label>{{ _("Uterine Contractions") }}</label>
				{{ doc.uterine_contractions }}</div>
			{% endif %}
			{% if doc.pain %}
			<div class="col-xs-4">
				<label>{{ _("Pain") }}</label>
				{{ doc.pain }}</div>
			{% endif %}
			{% if doc.losses %}
			<div class="col-xs-4">
				<label>{{ _("Losses") }}</label>
				{{ doc.losses }}</div>
			{% endif %}
			{% if doc.metrorrhagia %}
			<div class="col-xs-4">
				<label>{{ _("Metrorrhagia") }}</label>
				{{ doc.metrorrhagia }}</div>
			{% endif %}
			{% if doc.amniotic_liquid %}
			<div class="col-xs-4">
				<label>{{ _("Amniotic Liquid") }}</label>
				{{ doc.amniotic_liquid }}</div>
			{% endif %}
		</div>
		<br>
		{% endif %}
	</section>
	<section>
		{% if doc.uterine_height or doc.heart_noise or doc.foetal_presentation or doc.vaginal_examination %}
		<div class="row data-field">
			{% if doc.uterine_height %}
			<div class="col-xs-4">
				<label>{{ _("Uterine Height") }}</label>
				{{ doc.uterine_height }}</div>
			{% endif %}
			{% if doc.heart_noise %}
			<div class="col-xs-4">
				<label>{{ _("Heart Noise") }}</label>
				{{ doc.heart_noise }}</div>
			{% endif %}
			{% if doc.foetal_presentation %}
			<div class="col-xs-4">
				<label>{{ _("Foetal Presentation") }}</label>
				{{ doc.foetal_presentation }}</div>
			{% endif %}
			{% if doc.vaginal_examination %}
			<div class="col-xs-6">
				<label>{{ _("Vaginal Examination") }}</label>
				{{ doc.get_formatted("vaginal_examination") }}</div>
			{% endif %}
		</div>
		<br>
		{% endif %}
	</section>
	<section>
		{% if doc.notes %}
		<div>
			<h4>{{ _("Notes") }}</h4>
		</div>
		<div class="row data-field">
			<div class="col-xs-12">
				{{ doc.get_formatted("notes") }}</div>
		</div>
		<br>
		{% endif %}
	</section>

	<div id="footer-html" class="visible-pdf">
		{% if not no_letterhead and footer %}
		<div class="letter-head-footer">
			{{ footer }}
		</div>
		{% endif %}
		<p class="text-center small page-number visible-pdf">
			{{ _("Page {0} of {1}").format('<span class="page"></span>', '<span class="topage"></span>') }}
		</p>
	</div>
</div>
