{% include "templates/includes/folder_header.html" %}

<div class="body-html">
	{% if doc.memo %}
	<div class="row data-field">
		<div class="col-xs-12">
			<label>{{ _("Memo") }}</label>
			<p>{{ doc.get_formatted("memo") }}</p>
		</div>
	</div>
	<hr>
	{% endif %}
	<section>
		{% if doc.expected_term or doc.last_menstrual_period or doc.beginning_of_pregnancy or doc.gravidity or doc.parity or
		doc.preferred_location_for_delivery or doc.number_of_foetus %}
		<div>
			<h4>{{ _("Current Pregnancy") }}</h4>
		</div>
		<div class="row data-field">
			{% if doc.expected_term %}
			<div class="col-xs-4">
				<label>{{ _("Expected Term") }}</label>
				<p>{{ frappe.format_date(doc.expected_term) }}</p>
			</div>
			{% endif %}
			{% if doc.last_menstrual_period %}
			<div class="col-xs-4">
				<label>{{ _("Last Menstrual Period (LMP)") }}</label>
				<p>{{ doc.last_menstrual_period }}</p>
			</div>
			{% endif %}
			{% if doc.beginning_of_pregnancy %}
			<div class="col-xs-4">
				<label>{{ _("Beginning of Pregnancy") }}</label>
				<p>{{ doc.beginning_of_pregnancy }}</p>
			</div>
			{% endif %}
		</div>
		<br>
		<div class="row data-field">
			{% if doc.gravidity %}
			<div class="col-xs-4">
				<label>{{ _("Gravidity") }}</label>
				<p>{{ doc.gravidity }}</p>
			</div>
			{% endif %}
			{% if doc.parity %}
			<div class="col-xs-4">
				<label>{{ _("Parity") }}</label>
				<p>{{ doc.parity }}</p>
			</div>
			{% endif %}
			{% if doc.preferred_location_for_delivery %}
			<div class="col-xs-4">
				<label>{{ _("Preferred Location for Delivery") }}</label>
				<p>{{ doc.preferred_location_for_delivery }}</p>
			</div>
			{% endif %}
			{% if doc.number_of_foetus %}
			<div class="col-xs-4">
				<label>{{ _("Number of Foetus") }}</label>
				<p>{{ doc.number_of_foetus }}</p>
			</div>
			{% endif %}
		</div>
		<br>
		{% endif %}
	</section>
	<section>
		{% if doc.correspondents|length != 0 %}
		<div class="row">
			<div class="col-xs-12">
				<div>
					<h4>{{ _("Correspondents") }}</h4>
				</div>
				<table class="table table-condensed">
					<tr>
						<th>{{ _("Correspondent") }}</th>
						<th>{{ _("Name") }}</th>
						<th> {{ _("Location") }}</th>
					</tr>
					<tbody>
						{%- for row in doc.correspondents -%}
						<tr>
							<td style="width: 100px;">{{ row.correspondent }}</td>
							<td style="width: 200px;">{{ row.name1 }}</td>
							<td style="width: 200px;">{{ row.location }}</td>
						</tr>
						{% endfor %}
					</tbody>
				</table>
			</div>
		</div>
		<br>
		{% endif %}
	</section>
	<section>
		{% if doc.labs_results|length != 0 %}
		<div class="row">
			<div class="col-xs-12">
				<div>
					<h4>{{ _("Exam Results") }}</h4>
				</div>
				<table class="table table-condensed">
					<tr>
						<th>{{ _("Date") }}</th>
						<th>{{ _("Exam Type") }}</th>
						<th> {{ _("Exam Result") }}</th>
					</tr>
					<tbody>
						{%- for row in doc.labs_results -%}
						<tr>
							<td style="width: 50px;">{{ row.get_formatted("date") }}</td>
							<td style="width: 200px;">{{ _(row.exam_type) }}</td>
							<td style="width: 200px;">{{ row.exam_result }}</td>
						</tr>
						{% endfor %}
					</tbody>
				</table>
			</div>
		</div>
		<br>
		{% endif %}
	</section>
	<section>
