							{%- for row in doc.echography_results -%}
							{% if row.epf %}
							<td>{{ row.epf }}</td>
							{% else %}
							<td></td>
							{% endif %}
							{% endfor %}
						</tr>
						<tr>
							<th> {{ _("Morphology") }}</th>
							{%- for row in doc.echography_results -%}
							{% if row.morphology %}
							<td>{{ row.morphology }}</td>
							{% else %}
							<td></td>
							{% endif %}
							{% endfor %}
						</tr>
						<tr>
							<th> {{ _("Placenta") }}</th>
							{%- for row in doc.echography_results -%}
							{% if row.placenta %}
							<td>{{ row.placenta }}</td>
							{% else %}
							<td></td>
							{% endif %}
							{% endfor %}
						</tr>
						<tr>
							<th> {{ _("Aspect") }}</th>
							{%- for row in doc.echography_results -%}
							{% if row.aspect %}
							<td>{{ row.aspect }}</td>
							{% else %}
							<td></td>
							{% endif %}
							{% endfor %}
						</tr>
						<tr>
							<th> {{ _("Localization") }}</th>
							{%- for row in doc.echography_results -%}
							{% if row.localization %}
							<td>{{ row.localization }}</td>
							{% else %}
							<td></td>
							{% endif %}
							{% endfor %}
						</tr>
						<tr>
							<th> {{ _("Amniotic Fluid") }}</th>
							{%- for row in doc.echography_results -%}
							{% if row.amniotic_fluid %}
							<td>{{ row.amniotic_fluid }}</td>
							{% else %}
							<td></td>
							{% endif %}
							{% endfor %}
						</tr>
						<tr>
							<th> {{ _("Ombilical or Uterine Doppler") }}</th>
							{%- for row in doc.echography_results -%}
							{% if row.ombilical_or_uterine_doppler %}
							<td>{{ row.ombilical_or_uterine_doppler }}</td>
							{% else %}
							<td></td>
							{% endif %}
							{% endfor %}
						</tr>
						<tr>
							<th> {{ _("Foetal Presentation") }}</th>
							{%- for row in doc.echography_results -%}
							{% if row.foetal_presentation %}
							<td>{{ row.foetal_presentation }}</td>
							{% else %}
							<td></td>
							{% endif %}
							{% endfor %}
						</tr>
						<tr>
							<th> {{ _("Notes") }}</th>
							{%- for row in doc.echography_results -%}
							{% if row.notes %}
							<td>{{ row.notes }}</td>
							{% else %}
							<td></td>
							{% endif %}
							{% endfor %}
						</tr>
					</tbody>
				</table>
			</div>
		</div>
		<br>
		{% endif %}
	</section>
	<section>
		{% if doc.date or doc.delivery_term or doc.delivery_location or doc.delivery_way or doc.anesthesia or
		doc.placental_delivery or doc.scar or doc.anesthesia_complications %}
		<div>
			<h4>{{ _("Delivery") }}</h4>
		</div>
		<div class="row data-field">
			{% if doc.date %}
			<div class="col-xs-4">
				<label>{{ _("Date") }}</label>
				<p>{{ doc.get_formatted("date_time") }}</p>
			</div>
			{% endif %}
			{% if doc.delivery_term %}
			<div class="col-xs-4">
				<label>{{ _("Term (Weeks)") }}</label>
				<p>{{ doc.delivery_term }}</p>
			</div>
			{% endif %}
			{% if doc.delivery_location %}
			<div class="col-xs-4">
				<label>{{ _("Delivery Location") }}</label>
				<p>{{ doc.delivery_location }}</p>
			</div>
			{% endif %}
			{% if doc.delivery_way %}
			<div class="col-xs-4">
				<label>{{ _("Delivery Way") }}</label>
				<p>{{ doc.delivery_way }}</p>
			</div>
			{% endif %}
			{% if doc.anesthesia %}
			<div class="col-xs-4">
				<label>{{ _("Anesthesia") }}</label>
				<p>{{ doc.anesthesia }}</p>
			</div>
			{% endif %}
			{% if doc.placental_delivery %}
			<div class="col-xs-4">
				<label>{{ _("Placental Delivery") }}</label>
				<p>{{ doc.placental_delivery }}</p>
			</div>
			{% endif %}
			{% if doc.scar %}
			<div class="col-xs-4">
				<label>{{ _("Scar") }}</label>
				<p>{{ doc.scar }}</p>
			</div>
			{% endif %}
			{% if doc.anesthesia_complications %}
			<div class="col-xs-8">
				<label>{{ _("Complications") }}</label>
				<p>{{ doc.anesthesia_complications }}</p>
			</div>
			{% endif %}
		</div>
		<br>
		{% endif %}
	</section>
	<section>
		{% if doc.puerperium_complications or doc.current_medical_treatment or doc.contraception or doc.vaccination_details
		or
		doc.anti_d_immunoglobuline_details %}
		<div>
			<h4>{{ _("Postnatal Period") }}</h4>
		</div>
		<div class="row data-field">
			{% if doc.puerperium_complications %}
			<div class="col-xs-4">
				<label>{{ _("Puerperium Complications") }}</label>
				<p>{{ doc.puerperium_complications }}</p>
			</div>
			{% endif %}
			{% if doc.current_medical_treatment %}
			<div class="col-xs-4">
				<label>{{ _("Current Medical Treatment") }}</label>
				<p>{{ doc.current_medical_treatment }}</p>
			</div>
			{% endif %}
			{% if doc.contraception %}
			<div class="col-xs-4">
				<label>{{ _("Contraception") }}</label>
				<p>{{ doc.contraception }}</p>
			</div>
			{% endif %}
			{% if doc.vaccination_details %}
			<div class="col-xs-4">
				<label>{{ _("Vaccination") }}</label>
				<p>{{ doc.vaccination_details }}</p>
			</div>
			{% endif %}
			{% if doc.anti_d_immunoglobuline_details %}
			<div class="col-xs-4">
				<label>{{ _("Anti-D immunoglobulin") }}</label>
				<p>{{ doc.anti_d_immunoglobuline_details }}</p>
			</div>
			{% endif %}
		</div>
		<br>
		{% endif %}
	</section>
	<section>
		{% if doc.full_name or doc.gender or doc.birth_weight or doc.feeding_type or doc.neonatal_heel_prick or
		doc.release_date or doc.surveillance_points %}
		{% if doc.triplets %}
		<div>
			<h4>{{ _("First Newborn") }}</h4>
		</div>
		{% else %}
		<div>
			<h4>{{ _("Newborn") }}</h4>
		</div>
		{% endif %}
		<div class="row data-field">
			{% if doc.full_name %}
			<div class="col-xs-4">
				<label>{{ _("Full Name") }}</label>
				<p>{{ doc.full_name }}</p>
			</div>
			{% endif %}
			{% if doc.gender %}
			<div class="col-xs-4">
				<label>{{ _("Gender") }}</label>
				<p>{{ _(doc.gender) }}</p>
			</div>
			{% endif %}
			{% if doc.birth_weight %}
			<div class="col-xs-4">
				<label>{{ _("Birth Weight (g)") }}</label>
				<p>{{ doc.birth_weight }}</p>
			</div>
			{% endif %}
			{% if doc.feeding_type %}
			<div class="col-xs-4">
				<label>{{ _("Feeding Type") }}</label>
				<p>{{ _(doc.feeding_type) }}</p>
			</div>
			{% endif %}
			{% if doc.neonatal_heel_prick %}
			<div class="col-xs-4">
				<label>{{ _("Neonatal Heel Prick") }}</label>
				<p>{{ _(doc.neonatal_heel_prick) }}</p>
			</div>
			{% endif %}
			{% if doc.release_date %}
			<div class="col-xs-4">
				<label>{{ _("Release Date") }}</label>
				<p>{{ frappe.format_date(doc.release_date) }}</p>
			</div>
			{% endif %}
			{% if doc.surveillance_points %}
			<div class="col-xs-8">
				<label>{{ _("Surveillance Points") }}</label>
				<p>{{ doc.surveillance_points }}</p>
			</div>
			{% endif %}
		</div>
		<br>
		{% endif %}
	</section>
	<section>
		{% if doc.triplets and (doc.full_name_2 or doc.gender_2 or doc.birth_weight_2 or doc.feeding_type_2 or
		doc.neonatal_heel_prick_2 or doc.release_date_2 or doc.surveillance_points_2) %}
