html:
{% include "templates/includes/folder_header.html" %}

<div>
	<div id="footer-html" class="visible-pdf">
		{% if not no_letterhead and footer %}
		<div class="letter-head-footer">
			{{ footer }}
		</div>
		{% endif %}
		<p class="text-center small page-number visible-pdf">
			{{ _("Page {0} of {1}").format('<span class="page"></span>', '<span class="topage"></span>') }}
		</p>
	</div>
 {% if doc.memo %}
	<div class="row data-field">
		<div class="col-xs-12">
			<label>{{ _("Memo") }}</label>
			{{ doc.memo }}
		</div>
	</div>
	<hr>
 {% endif %}
 {% if doc.expected_term or doc.last_menstrual_period or doc.beginning_of_pregnancy or doc.gravidity or doc.parity or doc.preferred_location_for_delivery or doc.number_of_foetus %}
	<div><h4>{{ _("Current Pregnancy") }}</h4></div>
	<div class="row data-field">
		{% if doc.expected_term %}
		<div class="col-xs-4">
			<label>{{ _("Expected Term") }}</label>
			{{ doc.expected_term }}</div>
		{% endif %}
		{% if doc.last_menstrual_period %}
		<div class="col-xs-4">
			<label>{{ _("Last Menstrual Period (LMP)") }}</label>
			{{ doc.last_menstrual_period }}</div>
		{% endif %}
		{% if doc.beginning_of_pregnancy %}
		<div class="col-xs-4">
			<label>{{ _("Beginning of Pregnancy") }}</label>
			{{ doc.beginning_of_pregnancy }}</div>
		{% endif %}
	</div>
	<br>
	<div class="row data-field">
	 {% if doc.gravidity %}
		<div class="col-xs-4">
			<label>{{ _("Gravidity") }}</label>
			{{ doc.gravidity }}</div>
	 {% endif %}
	 {% if doc.parity %}
		<div class="col-xs-4">
			<label>{{ _("Parity") }}</label>
			{{ doc.parity }}</div>
		{% endif %}
	 	{% if doc.preferred_location_for_delivery %}
		<div class="col-xs-4">
			<label>{{ _("Preferred Location for Delivery") }}</label>
			{{ doc.preferred_location_for_delivery }}</div>
		{% endif %}
		{% if doc.number_of_foetus %}
		<div class="col-xs-4">
			<label>{{ _("Number of Foetus") }}</label>
			{{ doc.number_of_foetus }}</div>
		{% endif %}
	</div>
	<br>
	{% endif %}
	{% if doc.correspondents|length != 0 %}
	<div class="row">
		<div class="col-xs-12">
		<div><h4>{{ _("Correspondents") }}</h4></div>
		<table class="table table-condensed">
			<tr>
				<th>{{ _("Correspondent") }}</th>
				<th>{{ _("Name") }}</th>
				<th> {{ _("Location") }}</th>
			</tr>
			<tbody>
				{%- for row in doc.correspondents -%}
				<tr>
					<td style="width: 100px;">{{ row.correspondent }}</td>
					<td style="width: 200px;">{{ row.name1 }}</td>
					<td style="width: 200px;">{{ row.location }}</td>
				</tr>
				{% endfor %}
			</tbody>
		</table>
	</div>
	</div>
	<br>
	{% endif %}
	{% if doc.exam_results|length != 0 %}
	<div class="row">
		<div class="col-xs-12">
		<div><h4>{{ _("Exam Results") }}</h4></div>
		<table class="table table-condensed">
			<tr>
				<th>{{ _("Date") }}</th>
				<th>{{ _("Exam Type") }}</th>
				<th> {{ _("Exam Result") }}</th>
			</tr>
			<tbody>
				{%- for row in doc.exam_results -%}
				<tr>
					<td style="width: 50px;">{{ row.get_formatted("date") }}</td>
					<td style="width: 200px;">{{ row.exam_type }}</td>
					<td style="width: 200px;">{{ row.exam_result }}</td>
				</tr>
				{% endfor %}
			</tbody>
		</table>
	</div>
</div>
<br>
{% endif %}
{% if doc.echography_results|length != 0 %}
<div class="row">
	<div class="col-xs-12">
	<div><h4>{{ _("Echography") }}</h4></div>
	<table class="table table-condensed">
		<tbody>
		<tr>
			<th style="width: 75px;">{{ _("Date") }}</th>
			{%- for row in doc.echography_results -%}
			{% if row.date %}
			<td>{{ row.get_formatted("date") }}</td>
			{% else %}
			<td></td>
			{% endif %}
			{% endfor %}
		</tr>
		<tr>
			<th>{{ _("Q1") }}</th>
			{%- for row in doc.echography_results -%}
			{% if row.q1 %}
			<td>{{ row.q1 }}</td>
			{% else %}
			<td></td>
			{% endif %}
			{% endfor %}
		</tr>
		<tr>
			<th> {{ _("Craniocaudal Length") }}</th>
			{%- for row in doc.echography_results -%}
			{% if row.craniocaudal_length %}
			<td>{{ row.craniocaudal_length }}</td>
			{% else %}
			<td></td>
			{% endif %}
			{% endfor %}
		</tr>
		<tr>
			<th> {{ _("Nuchal Translucency") }}</th>
			{%- for row in doc.echography_results -%}
			{% if row.nuchal_translucency %}
			<td>{{ row.nuchal_translucency }}</td>
			{% else %}
			<td></td>
			{% endif %}
			{% endfor %}
		</tr>
		<tr>
			<th> {{ _("Morphology") }}</th>
			{%- for row in doc.echography_results -%}
			{% if row.morphology %}
			<td>{{ row.morphology }}</td>
			{% else %}
			<td></td>
			{% endif %}
			{% endfor %}
		</tr>
		<tr>
			<th> {{ _("Biometrics") }}</th>
			{%- for row in doc.echography_results -%}
			{% if row.biometrics %}
			<td>{{ row.biometrics }}</td>
			{% else %}
			<td></td>
			{% endif %}
			{% endfor %}
		</tr>
		<tr>
			<th> {{ _("Placenta") }}</th>
			{%- for row in doc.echography_results -%}
			{% if row.placenta %}
			<td>{{ row.placenta }}</td>
			{% else %}
			<td></td>
			{% endif %}
			{% endfor %}
		</tr>
		<tr>
			<th> {{ _("Ombilical or Uterine Doppler") }}</th>
			{%- for row in doc.echography_results -%}
			{% if row.ombilical_or_uterine_doppler %}
			<td>{{ row.ombilical_or_uterine_doppler }}</td>
			{% else %}
			<td></td>
			{% endif %}
			{% endfor %}
		</tr>
		<tr>
			<th> {{ _("Foetal Presentation") }}</th>
			{%- for row in doc.echography_results -%}
			{% if row.foetal_presentation %}
			<td>{{ row.foetal_presentation }}</td>
			{% else %}
			<td></td>
			{% endif %}
			{% endfor %}
		</tr>
		</tbody>
	</table>
</div>
</div>
<br>
{% endif %}
{% if doc.date or doc.delivery_term or doc.delivery_location or doc.delivery_way or doc.anesthesia or doc.placental_delivery or doc.scar or doc.anesthesia_complications %}
<div><h4>{{ _("Delivery") }}</h4></div>
<div class="row data-field">
	{% if doc.date %}
	<div class="col-xs-4">
		<label>{{ _("Date") }}</label>
		{{ doc.get_formatted("date_time") }}</div>
	{% endif %}
	{% if doc.delivery_term %}
	<div class="col-xs-4">
		<label>{{ _("Term (Weeks)") }}</label>
		{{ doc.delivery_term }}</div>
	{% endif %}
	{% if doc.delivery_location %}
	<div class="col-xs-4">
		<label>{{ _("Delivery Location") }}</label>
		{{ doc.delivery_location }}</div>
	{% endif %}
	{% if doc.delivery_way %}
	<div class="col-xs-4">
		<label>{{ _("Delivery Way") }}</label>
		{{ doc.delivery_way }}</div>
	{% endif %}
	{% if doc.anesthesia %}
	<div class="col-xs-4">
		<label>{{ _("Anesthesia") }}</label>
		{{ doc.anesthesia }}</div>
	{% endif %}
	{% if doc.placental_delivery %}
	<div class="col-xs-4">
		<label>{{ _("Placental Delivery") }}</label>
		{{ doc.placental_delivery }}</div>
	{% endif %}
	{% if doc.scar %}
	<div class="col-xs-4">
		<label>{{ _("Scar") }}</label>
		{{ doc.scar }}</div>
	{% endif %}
	{% if doc.anesthesia_complications %}
	<div class="col-xs-8">
		<label>{{ _("Complications") }}</label>
		{{ doc.anesthesia_complications }}</div>
	{% endif %}
</div>
<br>
{% endif %}
{% if doc.puerperium_complications or doc.current_medical_treatment or doc.contraception or doc.vaccination_details or doc.anti_d_immunoglobuline_details %}
<div><h4>{{ _("Postnatal Period") }}</h4></div>
<div class="row data-field">
 {% if doc.puerperium_complications %}
	<div class="col-xs-4">
		<label>{{ _("Puerperium Complications") }}</label>
		{{ doc.puerperium_complications }}</div>
	{% endif %}
	{% if doc.current_medical_treatment %}
	<div class="col-xs-4">
		<label>{{ _("Current Medical Treatment") }}</label>
		{{ doc.current_medical_treatment }}</div>
	{% endif %}
	{% if doc.contraception %}
	<div class="col-xs-4">
		<label>{{ _("Contraception") }}</label>
		{{ doc.contraception }}</div>
	{% endif %}
	{% if doc.vaccination_details %}
	<div class="col-xs-4">
		<label>{{ _("Vaccination") }}</label>
		{{ doc.vaccination_details }}</div>
	{% endif %}
	{% if doc.anti_d_immunoglobuline_details %}
	<div class="col-xs-4">
		<label>{{ _("Anti-D immunoglobulin") }}</label>
		{{ doc.anti_d_immunoglobuline_details }}</div>
	{% endif %}
</div>
<br>
{% endif %}
{% if doc.full_name or doc.gender or doc.birth_weight or doc.feeding_type or doc.neonatal_heel_prick or doc.release_date or doc.surveillance_points %}
<div><h4>{{ _("Newborn") }}</h4></div>
<div class="row data-field">
 {% if doc.full_name %}
	<div class="col-xs-4">
		<label>{{ _("Full Name") }}</label>
		{{ doc.full_name }}</div>
	{% endif %}
	{% if doc.gender %}
	<div class="col-xs-4">
		<label>{{ _("Gender") }}</label>
		{{ _(doc.gender) }}</div>
	{% endif %}
	{% if doc.birth_weight %}
	<div class="col-xs-4">
		<label>{{ _("Birth Weight (g)") }}</label>
		{{ doc.birth_weight }}</div>
	{% endif %}
	{% if doc.feeding_type %}
	<div class="col-xs-4">
		<label>{{ _("Feeding Type") }}</label>
		{{ _(doc.feeding_type) }}</div>
	{% endif %}
	{% if doc.neonatal_heel_prick %}
	<div class="col-xs-4">
		<label>{{ _("Neonatal Heel Prick") }}</label>
		{{ _(doc.neonatal_heel_prick) }}</div>
	{% endif %}
	{% if doc.release_date %}
	<div class="col-xs-4">
		<label>{{ _("Release Date") }}</label>
		{{ doc.release_date }}</div>
	{% endif %}
	{% if doc.surveillance_points %}
	<div class="col-xs-8">
		<label>{{ _("Surveillance Points") }}</label>
		{{ doc.surveillance_points }}</div>
	{% endif %}
</div>
{% endif %}
</div>
