html:
{%- macro add_header(page_num, max_pages, doc, letter_head, no_letterhead, footer, print_settings=None) -%}
{% if letter_head and not no_letterhead %}
<div class="letter-head">{{ letter_head }}</div>
{% endif %} {%- endmacro -%}

<div id="header-html" class="hidden-pdf">
	{{ add_header(0,1,doc,letter_head, no_letterhead) }}
</div>

<div class="print-heading">
	{% set patient = frappe.get_doc("Patient", doc.patient) %}
	<h2><strong>{{ _("Date : ") }}</strong>{{ doc.consultation_date }}</h2>
	<h2><strong>{{ _("Nom : ") }}</strong>{{ doc.patient_name }}</h2>
	<h2><strong>{{ _("Age : ") }}</strong>{{ patient.patient_age }}</h2>
</div>
{%- if doc.meta.is_submittable and doc.docstatus==0 and (print_settings==None or print_settings.add_draft_heading) -%}
<div class="text-center" document-status="draft">
	<h4 style="margin: 0px;">{{ _("DRAFT") }}</h4>
</div>
{%- endif -%} {%- if doc.meta.is_submittable and doc.docstatus==2-%}
<div class="text-center" document-status="cancelled">
	<h4 style="margin: 0px;">{{ _("CANCELLED") }}</h4>
</div>
{%- endif -%}
<div>
	<div id="footer-html" class="visible-pdf">
		{% if not no_letterhead and footer %}
		<div class="letter-head-footer">
			{{ footer }}
		</div>
		{% endif %}
		<p class="text-center small page-number visible-pdf">
			{{ _("Page {0} of {1}").format('<span class="page"></span>', '<span class="topage"></span>') }}
		</p>
	</div>

<div class="row">
		{%- for row in doc.drug_prescription_table -%}
		<div class="text-center">
			<h4>{{ row.drug }}</h4>
			<h4>{{ row.posology }}</h4>
			<h4>{{ row.pharmaceutical_form }}</h4>
			<h4>{{ row.treatment_duration }}</h4>
			<h4>{{ row.additional_notes }}</h4>
		</div>
		{%- endfor -%}
</div>
