css:
.col-xs-4, .col-xs-6, .col-xs-8, .col-xs-12 {
	padding-top: 15px;
}

table, tr, td, th, tbody, thead, tfoot, .col-xs-4, .col-xs-6, .col-xs-8, .col-xs-12, section {
	page-break-inside: avoid !important;
}

h1, h2, h3, h4, h5, h6, p {
	color: #434d56 !important;
}

label, div {
	color: #4f5a64 !important;
}

.print-heading {
	margin: 0.4in !important;
	text-align: left;
	text-transform: uppercase;
	padding-bottom: 20px;
	margin-bottom: 20px;
	border-bottom: 1px solid #d1d8dd;
}

.print-format {
	padding: 0;
	border-radius: 5px;
	margin: auto;
	position: relative;
}

.print-format th {
	background-color: #fff !important;
}

.header-html {
	padding: 0.4in;
	background: #f5fafc !important;
	border-radius: 5px 5px 0 0;
	width: 100%;
}

.body-html {
	padding: 0 0.8in 0.8in 0.8in;
}

.table > tbody + tbody {
	border-top: 1px solid #d1d8dd;
}

.table > tbody > tr > th {
	border-bottom: 1px solid #d1d8dd !important;
}

.table > tbody > tr > td {
	border-bottom: 1px solid #d1d8dd;
}

.vertical-table > tbody > tr > th {
	border-right: 1px solid #d1d8dd !important;
	width: 100px;
}

@media print {
	h1, h2, h3, h4, h5, h6, p, th, td, strong, a, b, ul, li {
		color: #434d56 !important;
	}
	.print-format {
	    padding: 0 25px;
	}
	.print-heading {
		margin: 0.4in !important;
		color: #657381 !important;
	}
	.header-html {
		background: #f5fafc !important;
	}
	.body-html {
		padding: 0 0.4in 0.4in 0.4in !important;
	}
	.letter-head-footer {
		padding-top: 20px;
	}
	a[href]:after {
		content: none;
	}
}

html:
{% include "templates/includes/folder_header.html" %}

<div class="body-html">
	<section>
		{% if doc.living_conditions or doc.father or doc.work or doc.sexuality or doc.family or doc.family_in_law %}
		<div>
			<h4>{{ _("Interview") }}</h4>
		</div>
		<div class="row data-field">
			{% if doc.living_conditions %}
			<div class="col-xs-6">
				<label>{{ _("Living Conditions") }}</label>
				{{ doc.get_formatted("living_conditions") }}</div>
			{% endif %}
			{% if doc.work %}
			<div class="col-xs-6">
				<label>{{ _("Work") }}</label>
				{{ doc.get_formatted("work") }}</div>
			{% endif %}
			{% if doc.father %}
			<div class="col-xs-6">
				<label>{{ _("Father") }}</label>
				{{ doc.get_formatted("father") }}</div>
			{% endif %}
			{% if doc.violences %}
			<div class="col-xs-6">
				<label>{{ _("Violences") }}</label>
				{{ doc.get_formatted("violences") }}</div>
			{% endif %}
			{% if doc.sexuality %}
			<div class="col-xs-6">
				<label>{{ _("Sexuality") }}</label>
				{{ doc.get_formatted("sexuality") }}</div>
			{% endif %}
		</div>
		<br>
		<div class="row data-field">
			{% if doc.family %}
			<div class="col-xs-6">
				<label>{{ _("Family") }}</label>
				{{ doc.get_formatted("family") }}</div>
			{% endif %}
			{% if doc.family_in_law %}
			<div class="col-xs-6">
				<label>{{ _("Family in Law") }}</label>
				{{ doc.get_formatted("family_in_law") }}</div>
			{% endif %}
		</div>
		<br>
		{% endif %}
	</section>
	<section>
		{% if doc.personal_background or doc.gynecological_background or doc.obstetrical_background %}
		<div>
			<h4>{{ _("Background") }}</h4>
		</div>
		<div class="row data-field">
			{% if doc.personal_background %}
			<div class="col-xs-12">
				<label>{{ _("Personal Background") }}</label>
				{{ doc.get_formatted("personal_background") }}</div>
			{% endif %}
			{% if doc.gynecological_background %}
			<div class="col-xs-12">
				<label>{{ _("Gynecological Background") }}</label>
				{{ doc.get_formatted("gynecological_background") }}</div>
			{% endif %}
			{% if doc.obstetrical_background %}
			<div class="col-xs-12">
				<label>{{ _("Obstetrical Background") }}</label>
				{{ doc.get_formatted("obstetrical_background") }}</div>
			{% endif %}
		</div>
		<br>
		{% endif %}
	</section>
	<section>
		{% if doc.pregnancy or doc.delivery %}
		<div>
			<h4>{{ _("Pregnancy and Delivery") }}</h4>
		</div>
		<div class="row data-field">
			{% if doc.pregnancy %}
			<div class="col-xs-6">
				<label>{{ _("Pregnancy") }}</label>
				{{ doc.get_formatted("pregnancy") }}</div>
			{% endif %}
			{% if doc.delivery %}
			<div class="col-xs-6">
				<label>{{ _("Delivery") }}</label>
				{{ doc.get_formatted("delivery") }}</div>
			{% endif %}
		</div>
		{% endif %}
	</section>


	<div id="footer-html" class="visible-pdf">
		{% if not no_letterhead and footer %}
		<div class="letter-head-footer">
			{{ footer }}
		</div>
		{% endif %}
		<p class="text-center small page-number visible-pdf">
			{{ _("Page {0} of {1}").format('<span class="page"></span>', '<span class="topage"></span>') }}
		</p>
	</div>
</div>
