css:
.col-xs-4, .col-xs-6, .col-xs-8, .col-xs-12 {
  padding-top: 15px;
}

table, tr, td, th, tbody, thead, tfoot, .col-xs-4, .col-xs-6, .col-xs-8, .col-xs-12 {
    page-break-inside: avoid !important;
}

html:
{% include "templates/includes/folder_header.html" %}

<div>
	<div id="footer-html" class="visible-pdf">
		{% if not no_letterhead and footer %}
		<div class="letter-head-footer">
			{{ footer }}
		</div>
		{% endif %}
		<p class="text-center small page-number visible-pdf">
			{{ _("Page {0} of {1}").format('<span class="page"></span>', '<span class="topage"></span>') }}
		</p>
	</div>
 {% if doc.living_conditions or doc.father or doc.work or doc.sexuality or doc.family or doc.family_in_law %}
	<div><h4>{{ _("Interview") }}</h4></div>
	<div class="row data-field">
		{% if doc.living_conditions %}
		<div class="col-xs-6">
			<label>{{ _("Living Conditions") }}</label>
			{{ doc.living_conditions }}</div>
		{% endif %}
		{% if doc.father %}
		<div class="col-xs-6">
			<label>{{ _("Father") }}</label>
			{{ doc.father }}</div>
		{% endif %}
		{% if doc.work %}
		<div class="col-xs-6">
			<label>{{ _("Work") }}</label>
			{{ doc.work }}</div>
		{% endif %}
		{% if doc.sexuality %}
		<div class="col-xs-6">
			<label>{{ _("Sexuality") }}</label>
			{{ doc.sexuality }}</div>
		{% endif %}
	</div>
	<br>
	<div class="row data-field">
	 {% if doc.family %}
		<div class="col-xs-6">
			<label>{{ _("Family") }}</label>
			{{ doc.family }}</div>
	 {% endif %}
	 {% if doc.family_in_law %}
		<div class="col-xs-6">
			<label>{{ _("Family in Law") }}</label>
			{{ doc.family_in_law }}</div>
			{% endif %}
	</div>
	<br>
	{% endif %}

{% if doc.personal_background or doc.gynecological_background or doc.obstetrical_background %}
<div><h4>{{ _("Background") }}</h4></div>
<div class="row data-field">
	{% if doc.personal_background %}
	<div class="col-xs-12">
		<label>{{ _("Personal Background") }}</label>
		{{ doc.personal_background }}</div>
	{% endif %}
	{% if doc.gynecological_background %}
	<div class="col-xs-12">
		<label>{{ _("Gynecological Background") }}</label>
		{{ doc.gynecological_background }}</div>
	{% endif %}
	{% if doc.obstetrical_background %}
	<div class="col-xs-12">
		<label>{{ _("Obstetrical Background") }}</label>
		{{ doc.obstetrical_background }}</div>
	{% endif %}
</div>
<br>
{% endif %}
{% if doc.pregnancy or doc.delivery %}
<div><h4>{{ _("Pregnancy and Delivery") }}</h4></div>
<div class="row data-field">
 {% if doc.pregnancy %}
	<div class="col-xs-6">
		<label>{{ _("Pregnancy") }}</label>
		{{ doc.pregnancy }}</div>
	{% endif %}
	{% if doc.delivery %}
	<div class="col-xs-6">
		<label>{{ _("Delivery") }}</label>
		{{ doc.delivery }}</div>
	{% endif %}
</div>
{% endif %}
</div>
